css:
@-moz-document domain("blendle.com") {
/*colors:
	background: #1B1B1B
	bg highlights: #313131
	text: #909090
	links: #468ACA
	darker links in blue buttons: #313131
	lighter links in red buttons: #909090
*/
.a-item,
.a-overlay,
.a-dialogue,
[class^="scrollable-"],
.element-wrapper [class^="item-"],
.item-suggestions-container,
.item-suggestions-title strong,
a[class^="refundItem"],
[class^="time-"],
.kiosk-list,
.v-horizontal-tile-pane,
.v-popular-in-issue .explanation h2 strong:not(.most-popular),
[class^="hideOnScroll"] .headroom,
a.main,
.app,
[class^="loadingWrapper"],
[class^="personalPage"],
[class^="timeOfDay"],
strong[class^="date"],
div[class^="intro"]>p,
.v-timeline
{
    background: #1B1B1B !important;
    color: #909090 !important;
}
[class^="sectionHeader"] h2[class^="title"]
{
	color: #909090 !important;
}
[class^="topBar-"],
button[class^="closeButton"],
.v-primary-navigation,
.v-primary-navigation-items a,
.v-notifications-dropdown .inner-handle,
.popular-item-link,
a[class^="purchaseNotice"],
[class^="_navigationBar_"],
[class^="form"],
input[class^="input"],
[class^="_navigationItem_"] > [class^="link"]{
    background-color: #313131 !important;
    color: #909090 !important;
}
[class^="_navigationItem_"] > [class^="link"].active {
	color: #ff6255 !important;
}
article.item:after {
    background: none !important;
}
.blendle-logo g.blendleText {
    fill: #909090
}
[class^="stickySharing-"] {
    display: none !important;
}

/*Kiosk page*/
.v-navigation,
.v-navigation a {
    background-color: #313131 !Important;
    color: #909090 !important;
    border-top-color: #313131 !important;
}
.v-notifications-dropdown .inner-handle {
    border-left: none !important;
}
.v-cover .favourite {
    background-color: #313131 !Important
}

/*the article listing page*/
.popular-item-link {
    border: none !important;
}

/*article cards need transparent text so the provider logo appears*/
.manifest-top-bar div,
.manifest-top-bar a {
    background-color: #313131 !important;
    border-color: #313131 !important;
}
.v-dropdown-list-button button {
    border-left: 1px solid #313131 !important;
}
.v-pane-tile.l-item .v-manifest {
    border-radius: 0px !important;
}
}
