css:
/*
 * Google News - just the articles
 *
 * Author      : GoldenEi
 * Updated     : 30. January 2009
 * Website     : http://unlastingdream.googlepages.com
 * Description : Shows the articles/storys on specific (editable) Google News-pages (removes the navigation bar, search field/buttons, header and footer
 * Usage       : Use with the Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@-moz-document url-prefix(http://news.google.de/?ned=tde),
               url-prefix(http://news.google.com/?ned=tde),
               url-prefix(http://news.google.de/news?),
               url-prefix(http://news.google.com/news?) {

#hl, #leftnav, #gbar, #gbi, #guser, #section-header, #gbh {
display:none !important; }

div[class="lt small versions"], div[class="rt"] {
display:none !important; }

div[class="basic-title b-0"],
div[class="basic-title b-1"], 
div[class="basic-title b-2"], 
div[class="basic-title b-3"], 
div[class="basic-title b-4"], 
div[class="basic-title b-5"], 
div[class="basic-title b-6"], 
div[class="basic-title b-7"],
div[class="basic-title b-8"],
div[class="bar c-0"],
div[class="bar c-1"], 
div[class="bar c-2"], 
div[class="bar c-3"], 
div[class="bar c-4"], 
div[class="bar c-5"], 
div[class="bar c-6"], 
div[class="bar c-7"],
div[class="bar c-8"] {
display:none !important; }

div[class="gaiaNav"] {
display:none !important; }

div[class="editionlist"] {
display:none !important; }

div[class="footer-disclaimer"] {
display:none !important; }

div[class="thumbnail"] {
display:none !important; }

div[class="bottom"] {
display:none !important; }

table[id="hp-sub-header"] {
display:none !important; }

div[class=""] {
display:none !important; }

div[class="search-label"] {
display:none !important; }

div[class="browse-sidebar"] {
display:none !important; }

div[id="alt-search-links"] {
display:none !important; }

td.leftnav {
display:none !important; }

table[id="hp-sub-header"] {
display:none !important; }

input[name="btnG"] {
display:none !important; }

input[name="q"] {
display:none !important; }

input[value="News-Suche"] {
display:none !important; }

input[name="btnmeta=search=search"] {
display:none !important; }

font[color="#aa0033"] {
display:none !important; }

center {
display:none !important; }

img[src="images/news_de_de.gif"], img[src="/images/news_de_de.gif"], img[src="http://www.gstatic.com/news/img/oldlogo_large/de_de/news.gif"] {
display:none !important; }

}
