css:
@-moz-document url("https://tweakers.net/"), url-prefix("https://tweakers.net/nieuws/"), url-prefix("https://tweakers.net/reviews/"), url-prefix("https://tweakers.net/downloads/") {
/*

Base COLORS: http://paletton.com/palette.php?uid=62N0K0kw0t-aCA18klC4J008-9P

FRONT:
-----

1. #12C100 --> green
1.1 #4b8645 --> green faded
1.2 #0d100c --> green max fade
2. #E8000C --> red
3. #85b6dc --> blue
3.1 #55758e --> blue faded

BACKGROUND
-----------

1. #000       --> black
1.1 #1a2319   --> green screen effect
2. #31402F    --> greenisch dark
3. #4D3839    --> redisch dark


This is styling that is usefull on all pages
*/

body,
html,
#menubar,
#menubottombar,
#menubottombar .menubottom {
    /* Apply prime color, and backgound to complete page, and menubar */
    
    background: #000;
    color: #12C100;
}
/* Remove boxshadow from menubar */

#menubar {
    box-shadow: none;
}
h1,
h2,
h3,
h4,
h5,
a {
    /* Primary color on headers, and headerlinks */
    
    color: #12C100 !important;
}
/* Make logo green */

#logo a {
    filter: hue-rotate(40deg);
}
#logo a:hover {
    background-color: #000;
}
/* Make visited article links less bright */

.useVisitedState a:visited {
    color: #4b8645 !important;
}
a:hover {
    color: #E8000C !important;
}
#searchbar {
    border: none;
    background: #000;
}
#bottom {
    /* 
    Remove footer (I never use it anyway) 
    Remove relevancy column, only takes up space ;)
    */
    
    display: none;
}
/* Apply green screen */

#contentArea {
    background: #0d100c !important;
    border-color: #31402F;
}
/* make image less bright and greyscale them */

#layout img {
    filter: grayscale(80%) brightness(65%);
}
/* Clean up buttons */

.fancyButton.grey,
.fancyButton.darkGrey {
    background-color: #000;
    border: 1px solid #31402F;
    color: #12C100;
    cursor: pointer;
    background-image: none;
    text-shadow: none;
}
.fancyButton.grey:hover,
.fancyButton.darkGrey:hover {
    background-color: #000;
    border: 1px solid #E8000C;
    background-image: none;
}
/* border colours of left content */

#bestBuyGuides li,
#recentlyTested li,
#bestBuyGuides ul,
#recentlyTested ul {
    border-color: #4b8645;
}
table.highlights .publicationTime span {
    color: #85b6dc;
}
#search input.text,
#newsletter input.text, #reactieFormContent {
    background: none;
    background-color: #000;
    font-size: 14px;
    color: #12C100;
    border: 1px solid #31402F;
}
.keywordSearch {
    border: #4b8645 solid 1px;
}
/* Remove sponsored stuff */

#sponsored,
.sponsored {
    display: none;
}
/* */

.userReviewDescription,
.darkFpBlock {
    background: none !important;
    background-image: none !important;
    background-color: #000 !important;
    border: 1px solid #31402F !important;
}

.thumb{
	background: none;
}

.modFilter .scoreButton {
    background: none ;
    background-image: none ;
    border: 1px solid darkgrey;
    margin-bottom: 2px;
}

.scoreButton.score-1,
.scoreButton.score-2 {
    background: none ;
    background-image: none ;
    border-color: #da3e02
}
.scoreButton.score0 {
    background: none ;
    background-image: none ;
    border-color: #d1b525
}
.scoreButton.score1 {
    background: none ;
    background-image: none ;
    border-color: #b6c600
}
.scoreButton.score2 {
    background: none ;
    background-image: none ;
    border-color: #60bf00
}
.scoreButton.score3 {
    background: none ;
    background-image: none ;
    border-color: #009d2c
}

/* Make search field fit design */
.keywordSearch input.submit {
    background-color: black;
    border-color: #4b8645;
}

#mainSearch{
	border-color: #4b8645;
}
}

@-moz-document url-prefix("https://tweakers.net/nieuws/"), url-prefix("https://tweakers.net/reviews/"), url-prefix("https://tweakers.net/downloads/") {
/* Article */

.relevancyColumn {
    /* 
    Remove footer (I never use it anyway) 
    Remove relevancy column, only takes up space ;)
    */
    
    display: none;
}

.nextPrevLinks.large .label{
    color: green;
}

.article {
    font-size: 12pt;
    font-style: normal;
    font-family: consolas;
    line-height: 1.6;
    overflow: hidden;
}

.articleColumn,
#reacties,
#commentColumn, .article table, .nextPrevLinks.large {
    width: 100%;
}
/* Reacties */

.reactieBody, .downloadDetails {
    background: #000;
    border: 1px solid #31402F;
}
.reactieBody.spotlight {
    background: #000;
    border-color: #E8000C;
}

.reactieBody.informative, .labelCard, .article table, .nextPrevLinks {
    background: #000;
    border: 1px solid #12C100;
}
#reacties #reactieContainer {
    border-left: 1px dotted #31402F;
    padding-left: 15px;
}

.thumb.usericonsmall.empty{
    display: none;
}

.reactieContent blockquote {
    border-left: 3px solid #85b6dc;
    color: #85b6dc;
    margin: 10px 0 10px 10px;
    padding-left: 5px;
    font-family: Consolas;
}
}

@-moz-document url("https://tweakers.net/") {
/* Frontpage */

.commentCount{
    background: none;
    color: #85b6dc !important;
}

.fpaItemsWrapper {
    border-bottom: 2px solid #4b8645;
    overflow: auto;
}
.greyTopBorderBlock {
    border-top: 2px solid #4b8645;
}
/* remove borders */

table.highlights,
.fpaItem {
    border: none;
}
table.highlights td {
    border-bottom: 1px solid #4b8645;
}
.mainColumn.frontpage {
    border-right: 1px solid #4b8645;
}
}
