css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bus67.ru") {
  body, input, div, ul, li, .form-group-sm .form-control, .btn {font-size: 3vw!important;}
  .form-group-sm .form-control, .btn {height:5.5vw}
  #leftColumn, #centerColumn, #rightColumn {display:block;position: relative;width: 95vw;right:auto;top:auto;bottom: auto;left: auto;}
  #leftColumn {margin-top:60px}
  .unit-icon-big b, .leaflet-marker-icon span, .unit-icon-small span, .unit-balloon * {font-size: 11px;transform:translateY(0%);}
  .panel-heading, .footer, .leftColumn .panel-group {display: none} 
  .leaflet-control-container {height: 85vh;width: 100%;}
}
