css:
@-moz-document domain("supremacy-rs.com") {
body {
    color: #ededed;
    background-color: #1c1c1c;
}
#ipbwrapper {
    background: #e3ae7b url("https://supremacy.harambwan.tech/static/img/bg.bd3df5e.jpg") no-repeat 50% 0 fixed;
}
#content {
    background: rgba(36, 36, 36, 0.98);
}
.ipsBox {
    background: rgba(23, 23, 23, 0.98);
}
.ipb_table {
    background: rgba(5, 3, 0, 0.97);
}
.unread {
    background: rgba(41, 28, 0, 0.98);
}
a {
    color: #b37b00;
}
.desc,
.desc.blend_links a,
p.posted_info {
    color: #949494;
}
.ipsType_pagetitle,
.ipsType_subtitle {
    color: #e0e0e0;
}
.ipsSideBlock {
    background: rgba(5, 3, 0, 0.97);
}
.ipsSideBlock h3 {
    color: #ededed;
}
.ipsSideBlock > h3:first-child {
    background: rgba(114, 73, 19, 1);
}
div .list_content > a:first-child {
    color: #e0e0e0;
}
.unread .altrow, .unread.altrow {
    background-color: inherit;
}
/* thread */

#ips_Posts {
    background: #1c1c1c;
}
.post_block {
    background: #1c1c1c;
}
.post_wrap h3 {
 	background: #39393a;   
}
.ipsBlockquote  {
    color: #000000;
}
.citation {
    color: #000000;
}
.ipsBox_container {
    background: #1c1c1c;
}
.post_body .post {
    color: #ededed;
}
}
