css:
@-moz-document domain("www.hltv.org")
{

/* background img & color */
  html,body
  {
    background-image: none !important;
    background-color: #070A14 !important;
  }

/* ads and crap */
  #i1_top,#i1_middle,#i4_bottom,#i0_forum,#matchpage_1
  {
    display: none !important;
  }
    
    

  .bgPadding {
      max-width: 1500px;
  }

  .widthControl {
      max-width: 1500px;
  }

  .leftCol {
      max-width: 250px;
      min-width: 250px;
  }

  .right2Col, .rightCol {
      max-width: 250px;
      min-width: 250px;
  }



}
