css:
@-moz-document domain("www.python.org") {
div {
    background-color: #190e00;
}
p, pre {
    color: #d1d1d1!important;
}
h2, h3 {
    color: #ddca7e !important;
}
strong {
    color: #ddca7e;
}
div {
    color: #aaa;
}
pre {
    background-color: #f4f4f4;
    color: #26211a;
}
.literal-block {
    color: #bcd9e5;
}
.toc-backref,
.reference {
    color: #ddca7e;
}
.page-title,
.timeline-Header-title {
    color: #ddca7e;
}
.field-name,
.label,
.timeline-footer,
.timeline-Header {
    background: #020e19;
}
.field-body,
td {
    background: #062d51;
}
.search-field {
    background: #190e00;
}
}
