@-moz-document domain("socialanxiety.0pk.ru"), domain("sociophobia.ru") {
/**
 * Name: Sociophobia.ru red CSS style
 * Version: 0.1 (18.05.2017)
 * Author: RapidDecompression
 */

/**
 * Repaint part - dirs as in original style:
 * COLOR VALUES ARE OVERWRITTEN!
 *
 * See section "mod part starts here" below!
 */

/* CS1 Background and text colours
-------------------------------------------------------------*/

body {
	background-color: #ffffee;
}

/* CS1.1 */
.punbb .section .container, .punbb .post-body, .punbb .post-links, .punbb td.tc2, .punbb td.tc3,
.punbb .formal fieldset .post-box, #viewprofile li strong, #viewprofile li div, #setmods dd,
.punbb .info-box, .punbb #pun-main .info-box .legend {
  background-color: #f0e0d6;
  color: #800000;
  }

/* CS1.2 */
.punbb .main .container, .punbb .post .container, .punbb .post h3, .punbb-admin #pun-admain .adcontainer {
  background-color: #f0e0d6;
  color: #800000;
  }

/* CS1.3 */
#pun-stats h2, .punbb .main h1, .punbb .main h2, #pun-debug h2, .punbb-admin #pun-admain h2 {
  background-color: #800000;
  color: #ffffee;
  }

/* CS1.4 */
#pun-title, #pun-title .container, .punbb .modmenu .container {
  background-color: #f0e0d6;
  color: #800000;
  }

/* CS1.5 */
.punbb legend span, #viewprofile h2 span, #profilenav h2 span, .punbb-admin #pun-admain legend span {
  background-color: #f0e0d6;
  color: #800000
  }

/* CS1.6 */
.punbb .post h3 span, .punbb th, #viewprofile li, #setmods dl  {
  background-color: #f0e0d6;
  color: #800000
  }

/* CS1.7 */
.punbb .quote-box, .punbb .code-box {
  background-color: rgba(142, 175, 56, 0.27);
  color: #117743
  }

/* CS1.8 */
#pun-navlinks .container {
  background-color: #800000;
  color: #ffffee;
  }

.punbb textarea, .punbb select, .punbb input {
  background-color: #ffffee;
  color: #800000
}

/* CS2 Border colours
-------------------------------------------------------------*/

/* CS2.1 */
.punbb .container, .punbb .post-body, .post h3, #pun-title {
  border-color: #800000 #800000 #800000 #800000
  }

/* CS2.2 */
.punbb .section, .punbb .forum, .punbb .formal, .punbb .modmenu, .punbb .info,
.punbb .category, .punbb .post {
  border-color: #ffffee
  }

/* CS2.3 */
#pun-stats h2, .punbb .main h1, .punbb .main h2, #pun-debug h2, .punbb-admin #pun-admain h2 {
  border-color: #800000;
  }

/* CS2.4 */
.punbb td, .punbb fieldset, #viewprofile ul, #profilenav ul, .punbb .post .post-body,
.punbb .post h3 span, .post-links ul, .post-links, .usertable table {
  border-color: #800000;
  }

/* CS2.5 */
.punbb th {
  border-color: #800000
  }

/* CS2.6 */
.punbb .quote-box, .punbb .code-box {
  border-color: #800000 #800000 #800000 #800000;
  }

#pun-ulinks li, #pun-announcement h2 span, li#onlinelist, #pun-help .formal .info-box h3.legend span {
  border-color: #800000
  }

#pun-ulinks li a, #pun-announcement h2, li#onlinelist div, #pun-help .formal .info-box h3.legend {
  border-color: #800000
  }

.punbb .divider {
  border-color: #800000 #800000 #800000 #800000
  }

.punbb .formal fieldset .post-box, .punbb .info-box {
  border: 1px solid #800000
  }

li.pa-online {
  border-left-color: #800000;
  }

.punbb .post-sig dt {
  border-top-color: #800000 !important;
  }


/* CS3 Links
-------------------------------------------------------------*/

/* CS3.1 */
.punbb a, .punbb a:link, .punbb a:visited,
.punbb-admin #pun-admain a, .punbb-admin #pun-admain a:link, .punbb-admin #punbb-admain a:visited {
  color: #5f0000;
  border-bottom: 0px none #0025e2;
  }

.punbb li.isactive a, .punbb li.isactive a:link, .punbb li.isactive a:visited {
  color: #af0000;
  }

/* CS3.2 */
.punbb a:hover, .punbb a:focus, .punbb a:active,  .punbb-admin #pun-admain .nodefault,
.punbb-admin #punbb-admain a:hover, .punbb-admin #punbb-admain a:focus, .punbb-admin #punbb-admain a:active {
  color: #e20000;
  border-bottom: 0px none #0025e2;
  }

/* CS3.3 */
#pun-navlinks a {
  color: #ffffee;
  border-bottom: 0px none #0025e2;
  }

/* CS3.4 */
#pun-navlinks a:hover, #pun-navlinks a:focus, #pun-navlinks a:active {
  color: #ffffee;
  border-bottom: 0px none #ffffee;
  }

#pun-pagelinks a:active, #pun-pagelinks a:focus {
  background-color: #800000;
  color: #ffffee;
  }

/* CS4 Post status icons
 -------------------------------------------------------------*/

/* CS4 Post status icons
 -------------------------------------------------------------*/

#pun-stats div.statscon {
        background-color: #f0e0d6;
}

#pun-crumbs2 .container,
#pun-about .container {
        background-color: #f0e0d6;
}

/**
 * vvv--- MOD part starts here ---vvv
 */

/**
 * Design fixes:
 */

.punbb {
    font-size: 11pt !important;
}

/* Width of the whole screen */
#pun_wrap {
	width: 90%;
    margin: 0 auto;
}

/* YOU! SHOULD!! **NEVER**!!! SEE!!! UNDERLINED!!! TEXT!!! AT ALL!!! */
.bbuline {
  text-decoration: none !important;
  font-weight: bold !important;
  font-style: italic !important;
}

/**
 * Make avatars 3d
 */
.pa-avatar > img {
  border: 1px solid #800000;
  border-radius: 5px;
  box-shadow: 0px 0px 1em 1px #300000;
}

/**
 * Hide elements:
 */

/* Hide header with name: */
#pun-title {
	display: none;
}

/* Hide other elements */
#pun-about,
#pun-crumbs2,
#pun-live-rusff {
	display: none;
}

}
