css:
@-moz-document domain("www.tumblr.com") {
.conversation-header,
.conversation-header-main,
.conversation-header-image {
    background: /*[[headerColor]]*/ !important;
}
.conversation-error label,
.conversation-header-main,
.conversation-header-image,
.timestamp,
.conversation-header-main,
.conversation-icebreakers,
.status-text {
    color: #FFFFFF !important;
}
.conversation-main {
    background: /*[[mainColor]]*/ !important;
}
.message-bubble,
.tx-scrollbar-thumb {
    background: /*[[bubbleColor]]*/ !important;
}
.conversation-compose button {
    color: /*[[buttonColor]]*/ !important;
}
}
