css:
@-moz-document domain("www.bom.gov.au"), regexp("http(s?)://(www.|)bom.gov.au") {
#columns, #header {
    background: #222;
}

#content,.day .forecast {
    background: none;
}

.forecast h3, .forecast, #content, dd.summary, h1, h2, h3, h4, h5, h6, strong {
    color: #eee;
}

p.alert, dd.rain, .date, .dt, .annotation, .pid, dd {
    color: #ccc;
    border-color: #666;
}
.min {
    color: #5f98f7;
}
.max {
	color: #f76e6e;
}

dd.rain em {
    color: #82b382;
}

#pageheight {
    background: #111;
}

#pagewrap {
    border: 0;
    box-shadow: 0 0 25px grey;
    padding-bottom: 0px;
}
#header .l_shadow {
    background: linear-gradient(to bottom, rgba(255,255,255,0.2) 0%,rgba(0,0,0,0) 100%);
}

.around {
    border-color: #666;
    box-shadow: 2px 2px 2px #444;
}
.around .first h3, .around table th, .around table td {
    background: none;
}

.around table {
    border-top-color: #666;
}
h2, .around table th {
    border-bottom-color: #666;
    border-top-color: #666;
}
p#next {
    border-top-color: #666;
}

p.warning a, p.warning a:visited {
    color: #eee;
    background-color: #444;
}
p.warning {
    border-color: #c7c2a0;
}
#footer-columns {
    background-color: #444;
    border-color: #666;
}
#footer a, #footer-columns li.icon-link a span {
    color: #aaa;   
    border-color: #666;
}
p.date {
    border-color: #666;
}
#content a:link, #content a:visited, a:visited {
    color: #6787a7;
}
.grayBorder {
    background-color: #1b455f;
}
.greenBorder {
    background-color: #294014;
}

div#content table.tabs table td, div#content table.tabs table th, table.viewer .border_item,  div#content table.tabs table{
 	background-color: #333;   
    border-color: #666;
}

table.viewer .radarLabel {
    background-color: #3f3f52;
    color: #6787a7;
}

table.viewer #control_form input[type=text], table.viewer #pointer input[type=text], table.viewer #coordinates input[type=text], table.viewer #offsets input[type=text], table.viewer .info input[type=text] {
    background: #222;
    color: #eee;
    border-color: #666;
}

table.viewer div, table.viewer span, table.viewer h2, table.viewer h3, table.viewer h4, table.viewer h5, table.viewer h6, table.viewer p, table.viewer blockquote, table.viewer pre, table.viewer a, table.viewer abbr, table.viewer code, table.viewer em, table.viewer img, table.viewer sub, table.viewer sup, table.viewer dl, table.viewer dt, table.viewer dd, table.viewer ol, table.viewer ul, table.viewer li, table.viewer fieldset, table.viewer form, table.viewer label, table.viewer legend, table.viewer table, table.viewer caption, table.viewer tbody, table.viewer tfoot, table.viewer thead, table.viewer tr, table.viewer th, table.viewer td {
    color: #eee;
}

table.viewer table.obstable td, table.viewer table.obstable th {
    background: none;
}

table.viewer div.info, div#content table.tabs td.activetab, div#content table.tabs td.links {
    background-color: #4d4d56;
    border-color: #666;
}
div#content table.tabs td.activetab a, div#content table.tabs td.activetab a:link, div#content table.tabs td.activetab a:active {
    color: #eee;
}

div#content table.tabs td.activetab a:hover, div#content table.tabs td.links {
    background-color: #64646f;
    color: #fff;
}

table.viewer {
    border-color: #666;
}

div#content table.tabs {
    background-color: #74747f;
    color: #eee;
}

input[type=button] {
    background: #444;
    color: #eee;
    border-color: #777;
}

div#content table.tabs td.notactivetab, div#content table.tabs td.activetab {
    border-color: #444;
}
#content .other {
    background: #edc319;
}
}
