@namespace url(http://www.w3.org/1999/xhtml);

/*----------uBlocko Panel----------*/
@-moz-document url(chrome://ublock0/content/popup.html) {
  body {
    background-color: #242C58 !important;
  }
  h2 {
    background-color: transparent !important;
    color: lightgray;
    font-weight: bolder;
    margin: 0em 0.2em 0em 0.2em !important;
  }
  p {
    color: darkgray;
    font-size: 85%;
  }
  #switch {
    margin: 4px 0;
  }
  #switch .fa {
    font-size: 35px !important;
    color: darkgreen;
  }
  body.off #switch .fa {
    color: dimgray;
  }
  #extraTools {
    background-color: transparent !important;
    margin: 6px 0 0 0 !important;
  }
  .tool:hover {
    color: #FFFFFF;
  }
  #extraTools > span:hover {
    color: #FFFFFF;
  }
  #page-blocked {
    font-size: 1.25em !important;
  }
  #basicTools {
    background-color: transparent !important;
    margin: 0 0 0.4em 0 !important;
  }
  .statName {
    background-color: transparent !important;
    opacity: 0.8 !important;
    margin: 0 0.2em 0.4em 0.2em !important;
  }
  .statValue {
    margin-top: 5px;
  }
  .tool {
    visibility: visible !important;
  }
  #tooltip {
    background-color: #C1C1B5;
  }
  .tool.enabled:hover {
    color: #FFFFFF;
  }
  #refresh {
    background-color: transparent;
    border: 1px solid transparent;
    color: black;
    font-weight: bolder;
    padding: 5px 0 0 4px;
  }
  #rulesetTools {
    color: black;
  }
  #rulesetTools > span {
    background-color: darkgray;
    border: 1px solid gray;
    border-radius: 5px;
    font-weight: bolder;
  }
  body.dirty #refresh:hover {
    color: darkred;
  }
  #firewallContainer.dirty ~ #rulesetTools > span:hover {
    color: maroon;
  }
  #firewallContainer > div {
    background-color: #3E5A5E !important;
  }
  #firewallContainer > div > span {
    border-bottom: 1px solid white !important;
    color: #FFFFFF !important;
  }
  #firewallContainer > div:hover {
    background-color: #507778 !important;
  }
  body.advancedUser #firewallContainer > div > span.noopRule {
    background-color: rgba(40, 50, 50, 0.4);
  }
  body.advancedUser #firewallContainer > div > span.noopRule.ownRule {
    background-color: rgba(40, 50, 50, 1);
  }
  #actionSelector > span:nth-of-type(2) {
    background-color: rgb(40, 50, 50);
  }
  body.advancedUser #firewallContainer.colorBlind > div > span.noopRule {
    background-color: rgba(30, 50, 50, 0.4);
  }
}

