css:
@font-face {
font-family: "Enigmatic Unicode";
src: "http://d.zaix.ru/5ats.ttf";
}

@-moz-document domain("vk.com"){

body{
color: #656565;
letter-spacing: 0.06em;
font-family: "Enigmatic Unicode";
background-image: url("http://www.designbolts.com/wp-content/uploads/2013/02/Free-Tileable-High-Quality-Wood-Textures-Patterns-For-3D-Mapping.jpg");
background-attachment: fixed;}

div.im-mess-stack--content ul.ui_clean_list.im-mess-stack--mess._im_stack_messages a, div.im-mess-stack--content span.im-mess-stack--tools a{
letter-spacing: 0.06em;
font-family: "Enigmatic Unicode";}

li{
letter-spacing: 0.06em;
font-family: "Enigmatic Unicode";
padding: -5px 0px;}

li.im-mess{
padding: 3px 30px 2px 0px;}

div.im_msg_text a, div.wall_post_text a, div.im-mess--text.wall_module._im_log_body a{
text-decoration: none;
color: #fab81b;
text-shadow: 0 0 0.1em rgba(228, 184, 80, 0.72);}

img.cell_img{
max-height:37px;
max-width:37px;}

div.nim-peer--photo._im_dialog_photo img{
max-height:47px;
max-width:47px;}

div.im_grid img, img.top_profile_img, .nim-peer--photo>img{
max-height:27.5px;
max-width:27.5px;}

div.nim-peer--photo a.im_grid img{
max-height:33px;
max-width:33px;}

div.nim-peer--photo._im_dialog_photo img, div.im_grid img, img.top_profile_img, div.nim-peer--photo a.im_grid img, img.chat_tab_img, img.fc_contact_photo, img.mail_box_single_ava_img, a.fc_msgs_img, img.post_img, img.post_field_user_image, img.people_cell_img, img.reply_img, .nim-peer--photo>img, img.cell_img{
border: 0.1em solid blue;} 

div #ads_left{
visibility: hidden;}

div.left_menu_nav_wrap{
position: relative;
top: -689px;}

}
