css:
@-moz-document domain("www.webnovel.com") {
div.g_header.cha-header.fs16,
div.cha-tools {
    opacity: 0;
    transition: opacity .2s .3s;
}
div.g_header.cha-header.fs16:hover,
div.cha-tools:hover {
    opacity: 1;
}
._color2 .cha-modal,
._color2 .g_chat_reply,
._color2 .g_msg+.g_msg,
._color2 .g_msg_quote,
._color2 .cha-header,
._color2 .cha-lst,
._color2 .cha-page-in,
.cha-page-in,
.g_header,
.c_strong,
body,
p,
._color3 .cha-ft a,
._color3 .cha-hd,
._color3 .cha-page,
._color3 .cha-tools a,
.g_mod_hd,
.theme-set-upDown,
.g_mod,
._color2 .cha-ft a,
._color2 .cha-hd,
._color2 .cha-page,
._color2 .cha-tools a,
._color2,
._color3,
.cha-fly,
html,
.g_hd_link,
._color3,
._color3 .cha-modal,
._color3 .g_chat_reply ._user,
._color3 .g_chat_reply textarea,
._color3 .cha-header,
._color3 .cha-lst,
._color3 .cha-page-in,
.cha-page,
.hom-h1,
.hom-h1 a,
.hom-h2,
.hom-h2 a,
.hom-h3,
.hom-h3 a,
.home-h-base,
.home-h-base a,
.hom-bd:before,
.hom-rec-1st,
.p20,
.g_footer,
.g_sub_hd,
.g_sub_hd p,
.g_score,
.det-hd,
.g_star small,
.m-terms,
.g_form_modal ._txtarea textarea,
.sub-hd,
 .content-list a {
    border-color: #001221!important;
    background-color: #001221!important;
    background: #001221!important;
    color: #DBDBDB!important;
    font-family: Georgia!important;
}
.lst-nav a {
    color: #7f7f7f!important
}
.lst-nav ._line {
    border-top: 1px solid #DBDBDB!important;
}
.det-con-ol {
    margin-bottom: 40px;
    border-top: 1px solid #DBDBDB!important;
}
.det-con-ol li {
    margin-bottom: 0px!important;
    border-bottom: 1px solid #7f7f7f!important;
}
.lst-nav ._on {
    background-color: #001221!important;
    background: #001221!important;
    color: #DBDBDB!important;
    font-family: Georgia!important;
}
.g_sub_hd ._shadow {
    font-size: unset!important
}
.cha-content p {
    margin-bottom: 1em;
}
.cha-page.j_contentWrap {
    width: auto;
}
.cha-page {
    max-width: 100%!important;
}
.cha-page-in {
    padding-left: 50px;
    padding: unset!important;
}
body {
    padding-left: 50px!important;
}
}
