css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 

domain("rarbg.to"),
domain("rarbg.com"),
domain("rarbgproxy.org") {
  *{
    color: #00ADEE !important;
  }
  body{    background-color: #000 !important;
}
  [data-_extension-text-contrast="bg"] {
    background-color: rgb(33, 25, 25) !important;
}

.lista-rounded, .lista, input, textarea,
.lista2 table tr td, .scrollspycomments div div,
.content-rounded
{
background:#2e3139 !important;
}
#pager_links b, #pager_links a:hover,
#searchTorrent, td.lista2, .searchTorrent,
.header2, .btn
{
background:#24262d !important;
}
  td.header5 {
    height: 28px;
    font-weight: 700;
    color: #00ADEE ;
    text-align: center;
    margin: 0;
    background: #26324d;
  }
  .lista-rounded{
     background-color: #24262d !important;
  }
  .searchTorrent, .formBox, .formBox2 {
    width: 100% !important;;
    padding: 1px !important;;
  }
  a.anal, a.anal:link, a.anal:active, a.anal:visited {
    color: #00ADEE  !important;
  }
  .block{display: none !important;}
  body > table:nth-child(4) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a:nth-child(1) > img:nth-child(1){margin: 0 0 0 0 !important; max-height: 40px !important;}
  .lista3{Padding: 0 0 0 0 !important;}
.btn-primary, .selected
{
background:#555555 !important;
}
.lista td, .searchTorrent, .formBox, .formBox2, input, select,
#pager_links b
{
border-color: #24262d !important;
}
#pager_links a, textarea,
.lista-rounded, .lista2, 
#description div div, #solve_string
{
border:1px #24262d solid !important;
}
table.lista2t tr.lista2 td, table.lista2t,
table.lista tbody tr td.lista
{
border-bottom:1px #24262d solid !important;
}
table.lista2t tr.lista2 td, table.lista2t, 
.td.header6, td.header6
{
border-right:1px #24262d solid !important;
}
table.lista2t
{
border-left:1px #24262d solid !important;
}
#description div div
{
background:#24262d !important;
}
.lista, font, body, .lista span, .lista a, .block,
#searchTorrent a, input, p, .lista2, .black,
.searchTorrent div, .lista td, .btn, .anal,
textarea, .ncomments_head, .ncomments_body,
.ncomments_user, .ncomments_votes, .selected a,
.ncomments_date, #pager_links a, #pager_links b,
td.header2 a, center div, .content-rounded,

  h2
{
color:#00ADEE !important;
}
  img[src*="over_opt"] {
    width: 200% !important;
    height: 200% !important;
    border: 1px solid #24262d !important;
    box-shadow: 15px 15px 7px 0px rgba(0,0,0,0.75);
    filter: contrast(1) brightness(1);
  }
  img[src*="over_opt"]:hover {
    z-index: 999 !important;
    position:relative !important;
    top: -10px;
    left: 1px;
    animation: neon2 1.5s ease-in-out infinite alternate;
    filter: contrast(1.25) brightness(1);
  }
  @-webkit-keyframes neon2 {
  from {
    box-shadow: 0 0 10px #203042, 0 0 20px #203042, 0 0 25px #203042, 0 0 30px #228DFF, 0 0 45px #228DFF, 0 0 50px #228DFF, 0 0 60px #228DFF, 0 0 85px #228DFF;
  }
  to {
    box-shadow: 0 0 5px #203042, 0 0 10px #203042, 0 0 15px #203042, 0 0 20px #228DFF, 0 0 35px #228DFF, 0 0 40px #228DFF, 0 0 50px #228DFF, 0 0 75px #228DFF;
  }
}

  }
