html {
    min-height: 100%;
}
td {
    transition: all 75ms ease-in-out;
}
body {
    height: 100%;
    color: #DEDEDE;
    max-width: 100% !important;
    background: #111111 !important;
    font-family: 'raleway', sans-serif !important;
    font-weight: 400 !important;
}
a {
    color: #DEDEDE !important;
    text-decoration: none;
    font-family: 'raleway', sans-serif !important;
    font-weight: 400 !important;
    transition: all 75ms ease-in-out;
}
.messageBody {
    background: #111111 !important;
}
.container {
    background: url('http://i.imgur.com/MUIixKM.png') no-repeat !important;
    background-size: 270px !important;
}
.mainUserInfoBox {
    position: relative;
    left: -10px;
    top: -30px;
}
.trPopupDialog {
    width: auto !important;
    padding: 10px !important;
    background: #111111;
}
.themeHeader {
background: #111111 !important;
transition: all 75ms ease-in-out;
}

.themeHeader a {
    color: #DEDEDE;
    font-family: 'raleway', sans-serif !important;
    font-weight: 400;
    font-size: 12px;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: #EEE;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title a {
    text-transform: lowercase;
    font-family: 'raleway', sans-serif !important;
    font-weight: 100 !important;
    font-size: 52px;
    z-index: 10 !important;
}
.themeHeader .navigation {
    padding: 0;
    height: 0;
    position: absolute;
    left: 100px;
    top: 85px;
    margin: 0;
    z-index: 0 !important;
}
.themeHeader a:hover {
    background: none !important;
    color: #A7ACD7 !important;
}
.navHome,
.navUpgrade,
.navMessages,
.navProfile,
.navFriends {
    background: none !important;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #EEE;
}
.subTitle {
    display: none;
}
.gwt-MenuBar {
    background: #111111;
}
.gwt-MenuItem-selected {
    background: #3A3C48;
}
.playerInfoPopup {
    background: #292b2c;
    color: #FFF;
    border: 1px red solid !important;
}
.playerInfoPopup a {
    color: #DEDEDE;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: #DEDEDE;
}
.main,
.themeContent {
    background: 0;
    background-color: #FFF;
    margin: 0 auto;
}

.mainViewport, .main, .themeContent, .statsView table {
    background: #111111 !important;
    transition: all 75ms ease-in-out;
}

.friendsTable .headerRow {
    font-size: 14px;
    text-transform: capitalize;
    font-style: normal !important;
}
.friendsTable tr:hover,
.friendsTable tr:focus {
    background-color: #E9E9E9;
}
.friendsTable .headerRow:hover,
.friendsTable .headerRow:focus {
    background-color: #F9F9F9;
}
.gwt-TabBar .gwt-TabBarItem {
    color: #333;
}
.mainUserHeader {
    background: none !important;
}
.footer {
    background: none;
    background-color: #111111;
    ;
    color: white;
    transition: all 75ms ease-in-out;
}
.footer a:hover {
    color: #A7ACD7;
    text-decoration: none;
    transition: all 75ms ease-in-out;
}
.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
