css:
@-moz-document domain("brick-hill.com")
{

  body
  {
    background-image: url("http://wp.production.patheos.com/blogs/standingonmyhead/files/2015/07/Hell.jpg") !important;
    background-size: cover !important;
    background-attachment: fixed !important;
  }

  #navbar
  {
    opacity: 1;
    background-color: #e51c19;
    padding: 10px;
    border-radius: 5px;
    margin-top: 5px;
    margin-bottom: 12px;
  }
/*display: none;*/

  #info
  {
    opacity: 1;
    background-color: #e51c19;
    padding: 10px;
    border-radius: 5px;
    margin-top: 5px;
    margin-bottom: 5px;
    /*display: none;*/;
  }

  #welcome
  {
    opacity: 1;
    background-color: #e51c19;
    padding: 10px;
    border-radius: 20px;
    margin-top: 5px;
    margin-bottom: 12px;
    /*display: none;*/;
  }

  #box
  {
    opacity: 1;
    background-color: #c60603;
    padding: 3px;
    border-radius: 6px;
    margin-top: 1px;
    margin-bottom: 4px;
    /*display: none;*/;
  }

  #title
  {
    opacity: 1;
    background-color: #d63900;
    padding: 10px;
    border-radius: 20px;
    margin-top: 5px;
    margin-bottom: 12px;
    /*display: none;*/;
  }

  #footer
  {
    opacity: 1;
    background-color: #161682;
    filter: grayscale(200%) blur(0.5px);
    padding: 10px;
    border-radius: 10px;
    margin-top: 5px;
    margin-bottom: 12px;
    /*display: none;*/;
  }

  element.style
  {
    border: 1px solid #b57500;
    background-color: #ffa500;
    color: #fff;
    text-align: center;
    padding: 3px;
    margin-top: 5px;
    border-radius: 10px;
  }

  #body
  {
    opacity: 1;
    background-color: #e51c19;
    padding: 10px;
    border-radius: 10px;
    margin-top: 5px;
    margin-bottom: 5px;
    /*display: none;*/;
  }

  .numButtonsHolder a
  {
    opacity: 1;
    background-color: #c60603;
    color: #fff;
    padding: 5px;
    border-radius: 2px;
    margin-top: 5px;
    margin-bottom: 5px;
    margin-left: -3px;
    /*display: none;*/;
  }

  #welcome
  {
    opacity: 1;
    background-color: #e51c19;
    padding: 10px;
    border-radius: 2px;
    margin-top: 0px;
    margin-bottom: 5px;
    margin-left: 3px;
  }

  th
  {
    background-color: #ef4107;
  }

  .thread-title
  {
    background-color: #ef4107;
    margin-bottom: 8px;
    padding: 10px;
    border-radius: 5px;
    margin-top: 0px;
    font-weight: bold;
  }

  #box
  {
    background-color: #f22626;
  }

  element.style
  {
    width: 150px;
    background-color: #c60603;
    border-radius: 3px;
  }

  .button
  {
    text-align: center;
    border-radius: 8px;
    background-color: #ff4c00;
    color: #fff;
    font-size: 15px;
    font-weight: bold;
  }

  #box
  {
    background-color: #c10f0f;
  }

  textarea
  {
    text-align: left;
    font-weight: bold;
  }

  input[type="button"],
input[type="submit"],
input[type="reset"],
input[type="file"]::-webkit-file-upload-button,
button,
select,
.button-style
  {
    background-color: #ff4c00;
    color: #fff;
    font-weight: bold;
    font-size: 15px;
    border-radius: 6px;
  }

  .shopDesc
  {
    font-size: 15px;
    font-weight: bold;
    color: #fff;
  }

  .shopTitle
  {
    font-size: 15px;
    font-weight: bold;
    color: #ff4c00;
    margin: -4px;
  }

  element.style
  {
    color: #fff;
  }

  #subsect
  {
    color: #fff;
    border-bottom-width: 6px;
    border-color: #870000;
  }

  h6
  {
    font-size: 12px;
    color: #c60603;
    font-weight: bold;
  }

  .shopSideBarButton
  {
    padding: 8px 0px 0px 20px;
    background-color: #822525;
    border-top: 1px solid #000;
    height: 25px;
    text-decoration: none;
  }

  h2
  {
    font-size: 30px;
    color: #000;
    margin: 5px;
  }

  #shopItem
  {
    border: 1px solid #000;
    background-color: #ea4d4d;
    width: 140px;
    height: 140px;
    border-radius: 10px;
  }

  .label
  {
    color: rgb(0, 0, 0);
    font-size: 16px;
    font-weight: bold;
    font-family: sans-serif;
  }

  element.style
  {
    width: 99.3%;
    height: 80px;
    margin: 0px;
    resize: vertical;
    border-radius: 16px;
    background-color: #e84e4e;
  }

  textarea
  {
    text-align: left;
    font-weight: bold;
    font-size: 15px;
    border-radius: 6px;
  }

  h5
  {
    font-size: 14px;
    color: #fff;
    margin: 5px;
  }
}
