@-moz-document url-prefix("https://soundcloud.com"), url-prefix("http://soundcloud.com") {
/* Gritter */

.gritter-item {
    padding: 4px 30px 3px 50px;
    overflow: hidden;
    cursor: pointer;
    background: #282F33;
    border: 1px;
    border-radius: 2px;
    color: white;
    opacity: 1;
}

.gritter-item-wrapper {
    box-sizing: border-box;
    position: relative;
    margin: 0 0 0px 0;
    width: auto;
    color: #333;
    background-color: transparent;
    min-height: 30px;
    border-radius: 0px;
    box-shadow: 0 0 0px rgba(0,0,0,0), inset 0 0 0px rgba(0,0,0,0);
    border: 0px solid #ccc;
}
/* */


#gritter-notice-wrapper {
    position: fixed;
    top: 60px;
    right: 30px;
    width: 301px;
    z-index: 9999
}
#gritter-notice-wrapper.top-left {
    left: 20px;
    right: auto
}
#gritter-notice-wrapper.bottom-right {
    top: auto;
    left: auto;
    bottom: 20px;
    right: 20px
}
#gritter-notice-wrapper.bottom-left {
    top: auto;
    right: auto;
    bottom: 20px;
    left: 20px
}

.gritter-item p {
    margin: 0
}
.oneLine .gritter-title,
.oneLine .gritter-item p {
    display: block;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    max-height: 23px
}
.gritter-title {
    font-weight: 700
}
.gritter-top,
.gritter-image {
    position: absolute;
    top: -1px;
    left: -1px;
    border-bottom-left-radius: 4px;
    border-top-left-radius: 4px
}
.gritter-image {
    width: 44px;
    box-shadow: inset 0 0 1px rgba(0, 0, 0, .4), inset 0 2px 0 rgba(255, 255, 255, .1)
}
.gritter-close {
    display: none;
    position: absolute;
    top: 0;
    right: 7px;
    width: 15px;
    height: 100%;
    border-radius: 50%;
    background: url(data:image/png;base64,) 50% no-repeat;
    cursor: pointer
}
.error .gritter-top,
.gritter-item-wrapper.big-success .gritter-top {
    width: 40px;
    height: 100%;
    background-position: center center, left top;
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .1);
    border: 1px solid #cc3914
}
.gritter-item-wrapper.error {
    min-height: 36px
}
.error .gritter-item {
    cursor: default
}
.no-title .gritter-item p,
.error .gritter-item p {
    max-height: none
}
.error .gritter-top {
    background: #ff4d00 url(https://a-v2.sndcdn.com/assets/images/notifications/error-18e777c.png) center center no-repeat;
    background-image: url(https://a-v2.sndcdn.com/assets/images/notifications/error-18e777c.png), linear-gradient(#f60, #ff4d00 40%, #f30)
}
.gritter-item-wrapper.big-success .gritter-top {
    background: #ff4d00 url(https://a-v2.sndcdn.com/assets/images/notifications/good-248c30c.png) center center no-repeat;
    background-image: url(https://a-v2.sndcdn.com/assets/images/notifications/good-248c30c.png), linear-gradient(#f60, #ff4d00 40%, #f30);
    background-size: 26px 26px, auto
}
@media (-webkit-min-device-pixel-ratio:2),
(min-resolution:2dppx),
(min-resolution:192dpi) {
    .gritter-item-wrapper.big-success .gritter-top {
        background: #ff4d00 url(https://a-v2.sndcdn.com/assets/images/notifications/good@2x-2dda7c7.png) center center no-repeat;
        background-image: url(https://a-v2.sndcdn.com/assets/images/notifications/good@2x-2dda7c7.png), linear-gradient(#f60, #ff4d00 40%, #f30);
        background-size: 26px 26px, auto
    }
}
@media (max-width:1079px) {
    #gritter-notice-wrapper {
        right: 20px
    }
}
}

