css:
@-moz-document domain(www.latech.edu) {

html{font-size:62.5%;}
body{
	font-family: Verdana, Arial, Helvetica, sans-serif;
	background: #d2d1c7 !important;
	margin:0; 
	padding:0;
	text-align:left !important; 
	color: #000;
}

#headerimage img{display:none;}

#container { 
	width:800px !important;
	margin:0 auto;
	border:10px solid #fff !important;
	border-top:none !important;
	background-color:#fff !important;
} 

#header { 
	height: 170px !important;
	width:800px;
	background:url('http://www.ug2.info/tech-css/header.png') no-repeat center top !important; 
	padding:0 !important;
} 
#header h1 {
	margin:0;
	padding:0 !important;
}
#pageoptions {
	position:absolute;
	top:10px;
	left:10px;
	width: 195px;
	background: transparent;
	padding: 0;
}
#textonly {
	color:#0d2f8b !important;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.8em !important;
	position:absolute;
	top:0 !important;
	left:1em;
	padding: 0;
}
#textonly a {
	color:#0d2f8b !important;
	text-decoration:none;
}
#textonly a:hover {
	color:#FF0000;
	text-decoration:underline;
}
#date {
	color:#FFFFFF;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.6em;
	position:absolute;
	top:2.5em;
	left:1em;
	background: transparent;
	padding: 0;
	display:none;
}
#date a {
	color:#FFFFFF;
	text-decoration:none;
}
#date a:hover {
	color:#FF0000;
	text-decoration:underline;
}
#weather {
	color:#FFFFFF;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.6em;
	position:absolute;
	top:4em;
	left:1em;
	background: transparent;
	padding: 0;
	display:none;
}
#weather a {
	color:#FFFFFF;
	text-decoration:none;
	display:none;
}
#weather a:hover {
	color:#FF0000;
	text-decoration:underline;
	display:none;
}
#headerimage {
	top:auto !important;
	left:auto !important;
	height:0;
	width:0;
	display:none;
}
#techsearch {
	position:absolute;
	top:150px !important;
	right:10px;
}
#techquicklinks {
	position:absolute;
	top:150px !important;
	right:250px !important;
}
#headernavbox {
	position:relative !important;
	top:110px !important;
	left:0 !important;
	width:800px !important;
}
#headernavboxabout {
	position:relative;
	top:109px !important;
	left:auto !important;
    height:1em !important;
	width:800px !important;
    padding:1em 0 !important;
}
#sidebar1 {
	width: 165px !important;
	background-color:#e0e7ee !important;
	padding: 10px !important;
	margin:1em 520px 1em -10px !important;
}
#applynow {
	padding:15px 0 10px 0;
	margin-left:.25em;
}
#homelink {
	padding:10px 0 10px 0;
	margin-left:.25em;
}
#sidebar2 {
	position:relative !important;
	top:0 !important;
	left:0 !important;
	float:left !important;
	width: 185px;
	background: transparent;
	padding: 10px 0 0 0 !important;
	margin:500px 0 0 -10px !important;
}
#mainContent { 
	position:relative;
	margin: 0 216px;
	padding: 10px 10px;
}
#mainContent table{font-size:1em;}
#mainContent table th{text-transform:uppercase; font-size:1.2em;}
#footer { 
	position:relative;
	padding: 0 10px 0 10px;
	background-color:#b3c0ca !important;
	margin:0 !important;
	text-align:center;
	clear:both !important;
	width:800px !important;
}
.fltrt {
	float: right;
	margin-left: 8px;
}
.fltlft {
	float: left;
	margin-right: 8px;
}
.editbutton {
	padding-top:15px;
}


/*Home Page Styles*/

#headernavboxabout, #headernavbox {
	background-color:#CB0100 !important;
	text-align:center;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1em !important;
	text-transform:uppercase;
	color:#FFF;
	font-weight:bold;
}
#headernavboxabout a {
	color:#FFFFFF;
	text-decoration:none;
}
#headernavboxabout a:hover {
	color:#FFFFFF;
	text-decoration:underline;
}
.about {
        text-align:left;
        position:absolute;
        top:5px;
        left:0;
        padding-left:10px;
}
.headerlinks {
        text-align:right;
        position:absolute;
        top:5px;
        right:0;
        padding-right:10px;
}
.newsarea {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#000000;
	font-weight:normal;
	font-size:1em !important;
	padding-left:0px;
}
.newsarea ul {
	list-style-type:none;
	margin-left:.25em;
}
.newsarea li {
}
.newsarea a {
	text-decoration:none;
	color:#002F8B;
}
.newsarea a:hover {
	text-decoration:underline;
	color:#FF0000;
}
.newslink {
        padding-bottom:5px;
        font-family:Verdana, Arial, Helvetica, sans-serif;
}
.newslink a{
        color:#002F8B;
        text-decoration:none;
}
.newslink a:hover {
        text-decoration:underline;
        color:#FF0000;
}


/*editor styles*/

#headernavbox {
	background:#FF0000;
	padding:5px 0 5px 0;
	text-align:center;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.65em;
	color:#FFFFFF;
	font-weight:bold;
}
#headernavbox a {
	color:#FFFFFF;
	text-decoration:none;
}
#headernavbox a:hover {
	color:#FFFFFF;
	text-decoration:underline;
}
.homefont {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#FFFFFF;
	font-weight:normal;
	font-size:.8em;
	padding-left:0px;
}
.homefont a {
	text-decoration:none;
	color:#FFFFFF;
}
.homefont a:hover {
	text-decoration:none;
	color:#FF0000;
}
.leftnav {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#0d2f8b !important;
	font-size:1.4em !important;
	padding:0;
}
.leftnav ul {
	list-style-type:none;
	margin:0 !important;
	padding:0 !important;
}
.leftnav li {
	display:block !important;
	padding-bottom:5px;
	font-size:1em !important;
	color:#cc0000;
}
.leftnav a {
	text-decoration:none;
	color:#0d2f8b !important;
}
.leftnav a:hover {
	text-decoration:underline;
	color:#181ea5 !important;
}
.rightnav {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#FFFFFF;
	font-weight:normal;
	font-size:1em;
	padding-left:10px;
}
.rightnav p{
	font-size:.8em;
}
.rightnav img {
	margin-bottom:30px;
}
.rightnav ul {
	list-style-type:none;
	font-size:.9em;
	margin-left:.25em;
	padding-left:.6em;
}
.rightnav li {
	padding-bottom:5px;
	font-size:.9em;
}
.rightnav a {
	text-decoration:none;
	color:#FFFFFF;
}
.rightnav a:hover {
	text-decoration:underline;
	color:#FF0000;
}
#pagetitle {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1em;
	color:#002F8B;
	font-weight:bold;
	border-bottom:1px solid #999999;
}
.contenttitle {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1em;
	color:#002F8B;
	font-weight:bold;
}
.title {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1.1em;
	color:#000000;
	font-weight:bold;
	padding:0 0 10px 0;
}
.author {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.9em;
	color:#000000;
}
.pubdate {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.9em;
	color:#000000;
	padding:0 0 20px 0;
}
.authoremail {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.9em;
	color:#000000;
	padding:0 0 20px 0;
}
.authoremail a {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#FF0000;
}
.authoremail a:hover {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	color:#FF0000;
	text-decoration:underline;
}
.pagecontent {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1.2em !important;
	color:#000000;
	margin:10px 0 0 0;
	text-align:left;
}
.pagecontent p {
	padding:0 0 .75em 0;
}
.pagecontent a {
	color:#002F8B;
	text-decoration:none;
}
.pagecontent a:hover {
	color:#FF0000;
	text-decoration:underline;
}
.pagecontent ul {
	margin-left:1.5em;
	padding-left:1.5em;
	padding-bottom:1em;
}
.pagecontent li {
	list-style-position:outside;
}
.pagecontent ol {
	margin-left:1.5em;
	padding-left:1.5em;
	padding-bottom:1em;
}
.pagecontent li {
	list-style-position:outside;
}
.pagecontent h1{
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1.2em;
	color:#002F8B;
        padding-bottom:10px;
}
.pagecontent h2{
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:1em;
	color:#FF0000;
        padding-bottom:10px;
}
.tableselect {
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.70em;
	color:#000000;
	margin:10px 0 0 0;
	text-align:left;
}
#footer p {
	margin: 0;
	padding: 10px 0;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.7em;
	color:#000058 !important;
	font-weight:normal;
}
#footer a {
	color:#808080 !important;
	text-decoration:none;
}
#footer a:hover {
	color:#666666 !important;
	text-decoration:underline;
}
.clearthefloats{
	clear:both;
}




/* ADMISSIONS */


.admissionsintro {
	height:100%;
	top:0px;
	left:0px;
	width:550px;
	padding-bottom:25px;
	border-bottom:1px solid #999999;
	margin-bottom:20px;
}
.admissionsannouncements {
	height:100%;
	position:relative;
	width:295px;
	float:left;
	padding:0 0 15px 0;
}
.admissionsrandomsmall {
	padding-bottom:0px;
	height:200px;
	position:relative;
	float:left;
}
.admissionsrandomlarge {
	float:right;
}
.admissionsathleticsshoutout {
	width:255px;
	background-color:#002F8B;
	height:325px;
	position:relative;
	float:right;
	top:0px;
	right:0px;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.9em;
	color:#FFFFFF;
	padding:10px;
	text-align:left;
}
.admissionsperks {
	width:200px;
	background-color:#888888;
	height:225px;
	position:relative;
	float:left;
	top:0px;
	right:0px;
	font-family:Verdana, Arial, Helvetica, sans-serif;
	font-size:.9em;
	color:#FFFFFF;
	padding:0 5px 0 0;
	text-align:left;
	margin-right:10px;
	margin-bottom: 5px;
}
.admissionsperks li{
list-style-type:square;
}

/* FYE STYLES */
.contentwrapper {
	position:relative;
}
.fyebanner{
	position:relative;
	top:0;
	left:0;
}
.fyetextbox{
	position:absolute;
	top:0;
	left:260px;
	width:260px;
}
.fyemissionbox{
	padding-bottom:5px;
}
.fyetenetbox{
	padding-bottom:10px;
}
.fyelegendofthebulldog{
}
}
