css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://goalll.nl/forum), url-prefix(http://www.goalll.nl/forum) {

/* Titels */

div[style="padding: 3px 0px 0px; width: 790px;"] {
background-color: transparent !important;
background: url(http://i40.tinypic.com/2cgjuq9.png) no-repeat top left !important;
margin-left: -11px !important;
padding-left: 10px !important;
padding-top: 4px !important;
width: 1001px !important;
}

#menubar {
display: none;
}

#datebar {
border-top: 4px solid #e6f7ff !important;
background-color: #bee5f6 !important;
}

.gensmall {
color: black !important;
}

a.forumlink {
color: #0099FF !important;
}

/* Quote, bijlages en codes */

.quotetitle {
border: 1px solid #CCCCCC !important;
background-color: #FFFFD9 !important;
color: #000000 !important;
text-decoration: underline !important;
}

.quotecontent {
background-color: #FFFFD9 !important;
border-left: 1px solid #CCCCCC !important;
border-right: 1px solid #CCCCCC !important;
border-bottom: 1px solid #CCCCCC !important;
}

.attachtitle {
border-color: #bee5f6 !important;
background-color: #bee5f6 !important;
color: #000000 !important;
}

.attachcontent {
border-color: #bee5f6 !important;
}

.codetitle {
margin: 10px 5px 0 5px !important;
padding: 2px 4px !important;
border-width: 0px 1px 0 1px !important;
border-style: solid !important;
border-color: #bee5f6 !important;
background-color: #bee5f6 !important;
color: #000000 !important;
background-image: none !important;
}

.codecontent {
border-color: #bee5f6 !important;
}

/* Overigen */

th {
background-color: #56B7E4 !important;
color: #000000 !important;
border-top: 4px solid #e6f7ff !important;
}

.cat {
background-color: #bee5f6 !important;
color: black !important;
height: 15px !important;
}

.catdiv {
background-color: #bee5f6 !important;
height: 15px !important;
}

.rtl .catdiv {
background-color: #bee5f6 !important;
}

.spacer {
background-color: #56B7E4 !important;
}

.btnbbcode {
color: black !important;
background-color: #bee5f6 !important;
border: none !important;
}

.legend {
display: none;
}

img {
vertical-align: middle !important;
}

div#main_content {
background-image: url(http://i43.tinypic.com/2jd3upv.jpg) !important;
background-position: left bottom !important;
}

div.white_curve_bottom {
display: none;
}

div.white_curve_top {
background-image: url(http://i40.tinypic.com/30kvcex.png) !important;
}

/* ~made by X_choice~ ~versie 0.5.5~ ~meer informatie: http://www.goalll.nl/forum/viewtopic.php?f=9&t=167~ */

}
