@-moz-document domain("nyaa.si") {

body > div.container > div.alert.alert-info, footer {
display:none!important;
}
body {
    color: grey;
    text-decoration:none;
        background: #202123 url("http://i.imgur.com/7lEdPj6.jpg");
    background-repeat: repeat;
    background-attachment: fixed;
}

a, a:link, a:active, a:visited, a:focus {
    color: grey!important;
        text-decoration:none;
}

a:link:hover, a:visited:hover {
    color: #fff!important;
    text-decoration: none;
}
/* table */

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #202123;
}
.table-striped>tbody>tr:nth-of-type(even) {
    background-color: #1d1e21;
}
body > div.container > div.table-responsive, .table-bordered>thead>tr>th, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>tbody>tr>td, .table-bordered>tfoot>tr>td, .table-bordered  {
    border-color: #000!important;
}
table.torrent-list tbody .comments, table.torrent-list tbody .comments:visited {
    position: relative;
    float: right;
    border: none;
    color: grey;
    padding: 0 5px;
    font-size: small;
    background-color: transparent;
}

/* label as in danger and successs */

.table>thead>tr>td.danger, .table>tbody>tr>td.danger, .table>tfoot>tr>td.danger, .table>thead>tr>th.danger, .table>tbody>tr>th.danger, .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>tbody>tr.danger>td, .table>tfoot>tr.danger>td, .table>thead>tr.danger>th, .table>tbody>tr.danger>th, .table>tfoot>tr.danger>th {
background-color: rgba(255, 0, 0, 0.04)!important;
}
.table>thead>tr>td.success, .table>tbody>tr>td.success, .table>tfoot>tr>td.success, .table>thead>tr>th.success, .table>tbody>tr>th.success, .table>tfoot>tr>th.success, .table>thead>tr.success>td, .table>tbody>tr.success>td, .table>tfoot>tr.success>td, .table>thead>tr.success>th, .table>tbody>tr.success>th, .table>tfoot>tr.success>th {
background-color: rgba(0, 255, 0, 0.04)!important;
}
.table-striped>tbody>tr:hover {
    -webkit-filter: brightness(150%); /* Safari */
    filter: brightness(150%);
}

