/* pagination */ 

.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
    z-index: 2;
    color: #fff;
    background-color: #000;
    border-color: #000;
}
.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    color: #fff;
    background-color: #35383f;
    border-color: #000;
    font-weight:bold;
}
.pagination>li>a, .pagination>li>span {
    color: grey;
    background-color: #202123;
    border: 1px solid #000;
}
.pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus {
    color: grey;
    background-color: #202123;
    border-color: #000;
}

/* navigation bar */

.btn-primary, .navbar-form .input-group {
    color: #fff!important;
    background-color: #121314!important;
    border-color: #000;
}
.btn-primary:hover {
    color: #fff!important;
    background-color: #202123!important;
    border-color: grey;
}
.form-control {
    color: #000;
    background-color: white!important;
}
.form-control:focus, .input-group-addon:not(:first-child):not(:last-child), .input-group-btn:not(:first-child):not(:last-child), .input-group .form-control:not(:first-child):not(:last-child), .navbar-form .input-group {
    border-color: grey;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(150, 150, 150, 0.6);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(150, 150, 150, 0.6);
}

/*  torrent info */

.panel-default {
    border-color: #000;
}
.panel, .panel-default>.panel-heading, .panel-footer {
    background-color: #202123!important;
    color: grey;
}
.btn-danger {
    color: #af9493;
    background-color: #632d2c;
    border-color: #632d2c;
}
.btn-success {
    color: grey;
    background-color: #35383f;
    border-color: #35383f;
