css:
@-moz-document domain("www.trifive.com") {
body {
    background: #363636;
}
.tborder {
    border: 1px solid #222222;
    background: #272727;
}
.alt1 {
    background: #272727;
    color: #979797;
}
.alt1Active {
    background: #272727;
}
.alt2 {
    background: #272727;
    color: #979797;
    border-left: 1px solid white;
}
.navbar > a {
    color: white;
}
a:visited,
body_avisited {
    color: #979797 !important;
    /* Force visited links to be lighter since next css rule makes all hrefs in fourm list white */
}
.alt1 > div > a {
    color: white;
}
a {
    color: white;
}
.tcat {
    background: #7d3f3d;
}
form > table > tbody > tr > .vbmenu_control {
    /* Super hacky way to stop all .vbmenu_contorls from becoming red an djust have forum actions red */
    
    background: #7d3f3d;
    border-left: 1px solid white;
}
.thead {
    background: #7d3f3d;
}
.thead[colspan="7"] {
    background: #7d3f3d;
}
.thead:not(:first-child) {
    border-left: 1px solid white;
}
.smallfont {
    color: #979797 !important;
}
a[target="_blank"] {
    color: white;
}
.page {
    background: #303030;
}
td[id^="td_post_"] {
    background: #272727;
}
table[id^="post"] > tbody > tr > .alt2 {
    background: #303030;
}
.panelsurround {
    background: #272727;
}
.panel {
    background: #242424;
    border: 0px;
}
.fieldset > legend {
    color: #979797;
}
label[for="qr_quickreply"] {
    color: #979797;
}
td > div > a > strong {
    color: #e0e0e0;
}
div > div > span > a > strong {
 	color: #e0e0e0;
}
a[rel="nofollow"] {
    color: #e0e0e0;
}
a[href^="member.php?u="] {
    color: #e0e0e0;
}
.smallfont > a {
    color: #e0e0e0;
}
}
