@-moz-document url-prefix("http://writer.dek-d.com/"), url-prefix("http://my.dek-d.com/"), url-prefix("https://writer.dek-d.com/") {
@font-face {
    font-family: 'Kanit';
    font-style: normal;
    font-weight: 400;
    src: local('Kanit'), local('Kanit-Regular'), url(https://fonts.gstatic.com/s/kanit/v1/XJlHi6uxFW6uak2UaVnLX4DGDUGfDkXyfkzVDelzfFk.woff2) format('woff2');
}
#toolbar .toolbar {
    background: #707070;
    position: fixed;
    top: 0;
    width: 100%;
    height: 30px;
    text-align: center;
    z-index: 20;
    font-size: 10px;
    color: #fff;
}
#toolbar .toolbar-container {
    width: 1260px;
    height: 30px;
    margin: 0 auto;
    background: #707070;
    box-shadow: inset 1px 0 0 0 #707070;
    border-left: 1px solid #707070;
}
#toolbar .tb-logo {
    height: 30px !important;
}
#toolbar .toolbar-menu-item {
    line-height: 29px;
    height: 30px;
    box-shadow: inset -1px 0 0 0 #707070;
    border-right: 1px solid #707070;
}
#toolbar .toolbar-user-menu {
    box-shadow: inset 1px 0 0 0 #707070;
    border-left: 1px solid #707070;
    height: 30px;
}

#toolbar .toolbar-user-menu .tb-user-thumb {
    width: 30px;
    height: 30px;
    position: relative;
    top: 0px;
    border: none !important;
    border-radius: 20px;
}

#toolbar .tb-icon-mainmenu {
    font-size: 14px;
    line-height: 32px;
}

p#big_text {
    background: none repeat scroll 0 0 #f37a01;
    outline: none;
    color: #FFFFFF;
    font-family: Helvetica, Thonburi, Kanit, Tahoma;
    font-size: 24px;
    font-weight: bold;
    height: 40px;
    line-height: 32px;
    text-align: center;
    width: 100%;
    padding-top: 5px;
}

.head1 {
    border: none !important;
	border-radius: 0px !important;}

.big_fb {
    width: 764px;
    margin: auto;
    border-top: solid 1px #ebebeb;
    padding-top: 12px;
    height: 59px;
    display: none;
}

.name_storybox {
    margin-top: 25px !important;
}

ul.name_story {
    margin: 20px 0 25px 35px !important;
}

p.vote_m {
    border-right: solid 1px #e5e5e5;
    color: #FFBA82 !important;
}
a.review,
a.name_sb01:hover {
    background: #000;
}
a.review,
a.name_sb01 {
    display: block;
    width: 45px;
    height: 45px;
    font-size: 10px;
    text-decoration: none;
    float: right;
    margin-right: 7px;
    line-height: 14px;
    padding-top: 10px;
    margin-left: 19px;
    background: none;
}
#sec-head,
#head,
#sec-menu,
#banner,
#banner-reccommend-desktop,
#banner-panorama.new-style .banner-wrapper,
.box04,
.box04_head,
.box05,
.box05_left,
.box04_page,
.toolbar-menu-item.track-ga,
.toolbar-menu-item.track-ga.tablet-version-hide,
div#CM_viewlong {
    display: none !important;
}

.toolbar-menu-item.menu-home.track-ga {
    display: block !important;
}

h3 .ribbon {
    background: none !important;
}

#main {
    margin: auto;
    width: 836px;
    background: #1d1c1c repeat;
}
#main-center {
    width: 836px;
    margin: 10px auto auto;
}
p.font_nu01 {
    text-align: center;
    font-size: 22px;
    /*color: #4b4b4b;*/
    color: #FFF;
    font-weight: bold;
}
.vote-sc {
    display: none !important;
}
.content {
    width: 836px;
    margin: 4px auto auto;
    background: #1d1c1c repeat;
    padding-top: 10px;
}
#story-content {
    padding: 20px 50px;
    box-sizing: border-box;
    width: 100%;
    margin: auto;
}
div.story {
   /* background-color: #F7F3F0; */
    background-color: #1d1c1c;
}

.writer-section-body {
    padding: 14px 16px;
    font-size: 14px;
    font-family: Helvetica, Thonburi, Leelawadee, Tahoma;
}

h2.box02,
.box01 {
    background: #fff repeat;
}
.box01_1 {
    margin-top: 0;
}
.box01,
h2.box02 {
    width: 836px;
    height: 35px;
    margin-top: 2px;
    padding-left: 20px;
    padding-top: 5px;
}
ul.box_uu01 li a {
    background: none !important;
    font-size: 10px;
    color: #000;
    display: block;
    float: left;
    height: 25px;
    line-height: 26px;
    border-right: solid 1px #DEDEDE;
    padding-right: 30px;
    margin-right: 30px;
}

.comment-setbubble {
    border: none;
}

.comment-action {
    background-color: #63615f;
    height: 30px;
    padding: 0 20px;
}

.avatar,
.user-pic-comment,
.arrow_box.social,
.banner_top_mobile {
    display: none !important;
}
.name-area,
.cm-time {
    font-size: 10px;
    padding-left: 20px;
    color: #FFBA82;
}

.others {
    width: 836px;
}

* {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
}
::selection {
    background: #000;
    color: #fff;
}
::-moz-selection {
    background: #fff;
    color: #fff;
}
html {
    min-width: inherit;
}
body {
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
    color: lightgray;
    background: #000 !important;
    background-image: none !important;
    margin: 0 auto;
    min-width: inherit;
}
p {
    font-family: Helvetica, Thonburi, "Kanit", tahoma;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: Helvetica, Thonburi, "Leelawadee", Tahoma;
}
h1,
h2 {
    color: #595959 !important;
}
h1 {
    font-size: 30px;
    line-height: 42px;
}
h2 {
    font-size: 22px;
    line-height: 26px;
}
hr {
    height: 1px;
    background-color: #d8d8d8;
    border: none;
    width: 100%;
    margin: 0px;
}
a[href] {
    color: #F2771F;
}
a[href]:hover {
    color: #707070;
}
a[href]:visited {
    color: #FFBA82;
}
#recommender-background {
    background: none repeat scroll 0 0 #E8E8E8;
    /* background: none repeat scroll 0 0 rgba(241, 209, 107, 0.42); */
    
    padding: 0px;
}
.app-footer {
    background: none repeat scroll 0 0 #DEDEDE;
    padding-top: 10px;
}
}

