@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("demonoid.pw") {

.header_tile, .ctable_header, .shadow_left, .shadow_right,
.ctable_top_left, .menu_tile, .header_right,
.ctable_bottom_left, .ctable_bottom_right,
.ending_top_left, .ending_tile_top, .ending_top_right,
.ending_tile_left, .ending_tile_bottom, .ending_bottom_left,
.ending_tile_right, .ending_bottom_right,
.menu_separator, .ctable_top_right
{
background-image: none !important;
}

.header_right
{
background-image: url("http://www.thermalzombie.com/stylish/red_background.png") !important;
background-repeat: no-repeat;
background-size:50px 50px !important;
background-attachment: relative !important;
background-position: -50px 1px !important;
z-index: 50 !important;
}
.demonoid
{
background-image: url(https://i.imgur.com/1j5THi8.png)!important
}
.demon 
{
background-image: url("http://www.thermalzombie.com/stylish/demonoid_logo.png") !important;
}
body, .demon, .main_content, input, textarea,
.ending_disclaimer, select
{
background-color: #212029 !important;
}
.torrent_header_2, .torrent_header_1, 
.bgg, #bbb, .nadsf
{
background-color: #444444 !important;
}
.ctable_content, .ctable_content_no_pad, .tone_1_bl,
.tone_2_bl, .tone_1_pad, .tone_2_pad
{
background-color: #222222 !important;
}
.ctable_bottom, .menu_tile, .added_today,
.ctable_header, .ctable_top_left, .ctable_top_right,
.ctable_bottom_left, .ctable_bottom_right
{
background-color: #57273B !important;
}
.main_content
{
border-color: #57273B !important;
}
.tone_3_bl, .tone_1_bl, .tone_2_bl, .added_today,
.torrent_header_1, .ctable_content_no_pad
{
border-color: #57273B !important;
}
input, select, textarea
{
border: 1px #57273B solid !important;
}
.ctable_content
{
border-left: 3px #57273B solid !important;
border-right: 3px #57273B solid !important;
}
.nadsf
{
border-top: 1px #57273B solid !important;
border-bottom: 1px #57273B solid !important;
}
.user_box, a, td, tr, .added_today,
.ctable_content_no_pad, input, textarea,
.ending_disclaimer, .ending_tile_bottom,
.pfooter, select
{
color: #FFFFFF !important;
}
.green
{
color: #00FF00 !important;
}
.blue
{
color: #0089FD !important;
}
.red
{
color: #FF0000 !important;
}
iitzz, gbucc, akgre, udowo
{
display: none !important;
}
}
