css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iichan.hk")
{
	html,body
	{
		background:  #242424 none;
		color:	     #e6e1dc;
		font-family: Segoe UI,arial,sans-serif;
		font-size:   11pt;
	}

	div[de-oppost], .reply
	{
		background:    #2b2b2b;
		color:         #e6e1dc;
		overflow:      auto;
		display:       block;
		width:         950px;
		margin-left:   auto;
		margin-right:  auto;
		margin-bottom: 4px;
		word-wrap:     break-word;
		padding:       6px;
		border-left:   1px solid  #5a647c;
		border-right:  1px solid  #5a647c;
		border-bottom: 1px solid  #5a647c;
		border-top:    1px solid  #5a647c;
	}

	div[de-thread], div[id*="thread-"]
	{
		width:         950px;
		margin-left:   auto;
		margin-right:  auto;
	}

	hr
	{
		border-color:  #5a657c;
		margin-top:    4px;
		margin-bottom: 4px;
		border-bottom: 0px;
	}

	a, a:visited
	{
		color: #6d9cbe;
		text-decoration: underline;
	}

	a:hover
	{
		color: #da4939;
		text-decoration: none;
	}
	
	a:visited:hover /*goberghen <3*/
	{
		color: #b6b3eb;
		text-decoration: none;
	}

	.adminbar
	{
		text-align: center;
		float:      none;
	}

	.logo
	{
		color: #b6b3eb;
	}

	.postblock
	{
		background:	   #2b2b2b;
		color:         #e6e1dc;
		border-left:   1px solid  #5a647c;
		border-right:  1px solid  #5a647c;
		border-bottom: 1px solid  #5a647c;
		border-top:    1px solid  #5a647c;
	}

	.theader /* заголовок в "Список тредов" */
	{
		background: #5a647c;
		color:	    #242424;
	}

	.prohibition
	{
		color: #da4939;
	}

	.doubledash
	{
		display: none;
	}

	.thumbnailmsg
	{
		font-size: 0.7em;
		color:     #b6b3eb;
	}

	.filetitle
	{
		font-size: 1em;
		color:     #b6b3eb;
	}

	.abbrev /* "сообщение слишком длинное" */
	{
		color: #9e9e9e;
	}

	.omittedposts /* "пропущено N сообщений" */
	{
		color: #9e9e9e;
	}

	.highlight
	{
		background: #2b2b2b;
		color:		#f9f7f3;
		border:		1px dashed #5a647e;
	}

	.replytitle
	{
		font-size: 1em;
		color:	   #da4939;
	}

	.postername
	{
		color: #519f50;
	}

	.postertrip
	{
		color: #ffc66d;
	}

	.commentpostername
	{
		color: #519f50;
	}

	.adminname
	{
		color: #da4939;
	}

	.unkfunc /* гринтекст */
	{
		color: #a5c261;
	}

	.spoiler
	{
		background: #5a647c;
		color:	    #5a647c;
	}

	.spoiler:hover
	{
		background: #5a647c;
		color:	    #e6e1dc;
	}

	/* внешний вид главной страницы */
	.greeting
	{
		color: #b6b3eb;
	}

	.news
	{
		background: #5a647c;
		color:	    #242424;
	}

	/* колонка слева, там где список досок */
	.category
	{
		background: #2b2b2b;
	}

	.logo a
	{
		color: #b6b3eb;
	}

	.sublogo a
	{
		color: #b6b3eb;
	}

	.header
	{
		background: #5a647c;
		color:      #242424;
	}

	.list
	{
		border-left:   1px solid  #5a647c;
		border-right:  1px solid  #5a647c;
		border-bottom: 1px solid  #5a647c;
	}

	.list div a
	{
		background: #2b2b2b;
		color:	    #9e9e9e;
	}

	.list div a:hover
	{
		background: #3e3e3e;
		color:	    #f9f7f3;
	}

	#captcha
	{
		filter: invert(15%) saturate(1.3);
	}
}
