css:
@-moz-document domain("en.wikipedia.org") {
/* REM definitions */

html {
	font-size: 16px;
}
@media screen and (min-width: 480px) {
	html {
		font-size: 13px;
	}
}
@media screen and (min-width: 768px) {
	html {
		font-size: 15px;
	}
}
@media screen and (min-width: 1280px) {
	html {
		font-size: 17px;
	}
}
@media screen and (min-width: 1440px) {
	html {
		font-size: 19px;
	}
}
@media screen and (min-width: 1920px) {
	html {
		font-size: 20px;
	}
}
@media screen and (min-width: 2300px) {
	html {
		font-size: 22px;
	}
}
@media screen and (min-width: 2750px) {
	html {
		font-size: 25px;
	}
}
/* Base Elements */

ol {
	margin: 0.3rem 0 0 3.2rem;
}
ul {
	margin: 0.3rem 0 0 1.6rem;
}
li {
	margin-bottom: 0.1rem;
}
blockquote {
	margin: 1rem 0;
	padding: 0 2.5rem;
	background: ghostwhite;
	font-family: 'Linux Libertine', 'Georgia', 'Times', serif;
}
h1,
h2 {
	margin-bottom: 0.6rem;
}
/* Wiki style overrides */

#mw-page-base {
	height: 5rem;
}
#mw-head-base {
	margin-top: -5rem;
	margin-left: 10rem;
	height: 5rem;
}
.mw-body {
	margin-left: 10rem;
}
#right-navigation {
	margin-top: 2.5rem;
}
#left-navigation {
	margin-left: 10rem;
	margin-top: 2.5rem;
	margin-bottom: -2.5rem;
}
.mw-notification-area {
	top: 7rem;
	font-size: 0.8rem;
	padding: 1rem;
	width: 20rem;
	line-height: 1.25rem;
}
#siteNotice {
	font-size: 0.8rem;
}
.mw-body-content {
	line-height: 1.3rem;
	font-size: 0.875rem;
}
.mw-body h1,
.mw-body-content h1 {
	font-size: 1.8rem;
}
.mw-body-content h2 {
	font-size: 1.5rem;
	margin-top: 1rem;
}
.mw-body-content h3 {
	font-size: 1.2rem;
}
.mw-body-content h3,
.mw-body-content h4,
.mw-body-content h5,
.mw-body-content h6 {
	line-height: 1.35rem;
	margin-top: 0.3rem;
}
.mw-body-content p {
	margin: 1rem 0;
}
div.tright {
	margin: 0.5rem 0 1.3rem 1.4rem;
}
div.tleft {
	margin: 0.5rem 1.4rem 1.3rem 0;
}
html .thumbcaption {
	line-height: 1.25rem;
	padding: 0.1875rem;
}
div.thumbinner {
	padding: 0.1875rem;
}
.infobox {
	margin: 0.5rem 0 0.5rem 1rem;
	padding: 0.2rem;
	font-size: 88%;
	line-height: 1.25rem;
}
.infobox caption {
	padding: 0.2rem;
}
#toc,
.toc {
	padding: 0.5rem;
}
#toc ul,
.toc ul {
	margin: 0.3rem 0;
}
div.hatnote {
	padding-left: 1.6rem;
	margin-bottom: 0.5rem;
}
blockquote.templatequote div.templatequotecite {
	line-height: 1.25rem;
	padding-left: 1.6rem;
	margin-bottom: 1.25rem;
}
table.wikitable > tr > th,
table.wikitable > tr > td,
table.wikitable > * > tr > th,
table.wikitable > * > tr > td {
	padding: 0.2rem 0.4rem;
}
table.wikitable {
	margin: 1rem 0;
}
.mw-content-ltr ul,
.mw-content-rtl .mw-content-ltr ul {
	margin: 0.3rem 0 0 1.6rem;
}
ol.references,
div.reflist,
div.refbegin {
	margin-bottom: 0.5rem;
}
.mw-content-ltr ol,
.mw-content-rtl .mw-content-ltr ol {
	margin: 0.3rem 0 0 3.2rem;
}
.navbox-title .navbar {
	margin-right: 0.5rem;
}
.navbox-group,
.navbox-title,
.navbox-abovebelow {
	padding: 0.25rem 1rem;
	line-height: 1.25rem;
}
.collapseButton {
	margin-left: 0.5rem;
}
div#footer ul li {
	padding-top: 0.5rem;
	padding-bottom: 0.5rem;
	font-size: 0.7rem;
}
div#footer #footer-info li {
	line-height: 1.25rem;
}
div#footer #footer-places li {
	margin-right: 1rem;
	line-height: 2rem;
}
div#footer #footer-icons li {
	margin-left: 0.5rem;
	line-height: 1.5rem;
}
table.ambox-content {
	border-left: 0.625rem solid #f28500;
}
table.ambox-style {
	border-left: 0.625rem solid #fc3;
}
.ambox td.mbox-image {
	padding: 0.125rem 0 0.125rem 0.5rem;
}
.ambox th.mbox-text,
.ambox td.mbox-text {
	padding: 0.25rem 0.5rem;
}
.mw-content-ltr .magnify {
	margin-left: 0.1875rem;
}
div.vectorTabs {
	height: 2.5rem;
}
div.vectorTabs span {
	overflow: hidden;
}
div.vectorTabs ul li {
	line-height: 1rem;
}
div.vectorTabs li a {
	height: 1.4rem;
	padding-left: 0.75rem;
	padding-right: 0.75rem;
	font-size: 0.8rem;
}
div.vectorTabs span a {
	padding-top: 1.125rem;
}
div#mw-head div.vectorMenu h3 {
	font-size: 1rem;
	height: 2.5rem;
}
div.vectorMenu div.menu {
	top: 2.5rem;
}
#p-personal {
	position: absolute;
	top: 0.33rem;
	right: 0.75rem;
	z-index: 100;
}
#p-personal ul {
	padding-left: 10rem;
}
#p-personal li {
	line-height: 1.125rem;
	margin-left: 0.75rem;
	margin-top: 0.5rem;
	font-size: 0.75rem;
}
.catlinks {
	padding: 0.3125rem;
	margin-top: 1rem;
}
.catlinks li {
	line-height: 1rem;
	margin: 0.125rem 0;
	padding: 0 0.5rem;
}
.catlinks li:first-child {
	padding-left: 0.25rem;
}
html body.mediawiki .mbox-small-left {
	margin: 0.25rem 1rem 0.25rem 0;
}
.skin-vector #p-lang .uls-settings-trigger {
	margin-top: 0.25rem;
}
#p-search {
	margin-right: 0.5rem;
	margin-left: 0.5rem;
}
div#simpleSearch #searchInput {
	padding: 0.2rem 0 0.2rem 0.2rem;
	font-size: 0.8125rem;
}
div#mw-panel {
	top: 10rem;
	width: 10rem;
	padding-top: 1rem;
	box-sizing: border-box;
}
div#mw-panel div.portal {
	margin: 0 0.6rem 0 0.7rem;
	padding: 0.25rem 0;
}
div#mw-panel div.portal h3 {
	font-size: 0.75rem;
	margin: 0.5rem 0 0 0;
	padding: 0.25rem 0 0.25rem 0.7rem;
}
div#mw-panel div.portal div.body {
	margin: 0 0 0 0.6rem;
}
div#mw-panel div.portal div.body ul {
	padding: 0.3rem 0 0 0;
}
div#mw-panel div.portal div.body ul li {
	line-height: 1rem;
	padding: 0.25rem 0;
	font-size: 0.75rem;
}
#p-logo {
	top: -10rem;
	width: 10rem;
	height: 10rem;
	box-sizing: border-box;
}
#p-logo a {
	width: 10rem;
	height: 10rem;
	background-size: contain;
}
.mw-content-ltr .toc ul ul,
.mw-content-ltr #toc ul ul,
.mw-content-rtl .mw-content-ltr .toc ul ul,
.mw-content-rtl .mw-content-ltr #toc ul ul {
	margin: 0 0 0 2rem;
}
html body.mediawiki .mbox-small {
	margin: 0.25rem 0 0.25rem 1rem;
	width: 14.875rem;
}
td.mbox-image {
	padding: 0.125rem 0 0.125rem 0.9rem;
}
th.mbox-text,
td.mbox-text {
	padding: 0.25rem 0.9rem;
}
.mw-body .mw-indicators {
	padding-top: 0.4rem;
}
.infobox.sisterproject {
	width: 20rem;
}
/* Responsive overrides */

@media screen and (min-width: 982px) {
	#mw-head-base {
		margin-left: 12rem;
	}
	.mw-body {
		margin-left: 12rem;
		padding: 1.25rem 1.5rem 1.5rem 1.5rem;
	}
	#left-navigation {
		margin-left: 12rem;
	}
	#p-personal {
		right: 1rem;
	}
	#p-personal ul {
		padding-left: 12rem;
	}
	#p-search {
		margin-right: 1rem;
	}
	div#mw-panel {
		top: 12rem;
		width: 12rem;
	}
	#p-logo {
		top: -12rem;
		width: 12rem;
		height: 12rem;
		padding: 1.5rem;
		left: 0;
	}
	#p-logo a {
		width: 9rem;
		height: 9rem;
	}
}
/* Custom responsive */

@media screen and (min-width: 1300px) {
	.mw-body {
		padding: 1.5rem 2rem 2rem 1.75rem;
	}
}
}
