@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {

  
  .background-white, .badge-entry-toolbar-sticky, .CS3, .badge-item-vote-container span
  {
    background-color:#151515!important;
  }
  
  .badge-item-vote-container, .badge-more-share-button, .horizontal-vote li a.up, .horizontal-vote li a.down
  {
    background-color:#151515!important;
    border-color:#454545 !important;
  }
  
  .post-afterbar-meta
  {
    border-top: 1px solid #454545;
  }
  
  .CS3 .tab-bar
  {
    border-bottom: 1px solid #454545;
  }
  
  .badge-item-title, .badge-evt, .CS3 .tab-bar-left h3, .CS3 .tab-bar-right ul.tab li.active a, .CS3 .comment-entry .payload .username
  {
    color:#CCCCCC!important;
  }
  
  .CS3 .comment-entry .payload
  {
    color:#A0A0A0!important;
  }
  
  .CS3 .comment-box .payload .textarea-container textarea, .CS3 .comment-box .action
  {
    background-color:#222222!important;
    color:#CCCCCC!important;
    border-color:black !important;
  }
  .modal.idle {
    background:#202020!important;
  }
  section#idle h2, section#idle .ticker .info h3 {
    color:#CCCCCC!important;
  }
  section#idle .ticker {
    border:solid 1px #505050 !important;
  }
  section.modal a.btn-close {
    filter: invert(80%);
  }
}
