css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fimfiction.net") {
  .user_toolbar {
    background: linear-gradient(to bottom, #7f8e54 0%, #73804c 100%);
    border-bottom: 1px solid #616c40;
    box-shadow: unset;
    border-color: rgba(0,0,0,0.2);
  }
  
  .user_toolbar > ul {
  	text-align: center;
  }
  
  .user_toolbar > ul > li:first-of-type {
    border-left: 1px solid #677344;
  }
  
  .user_toolbar > ul > li {
    background: linear-gradient(to bottom, #88985a 0%, #7f8e54 100%);
    text-shadow: 1px 1px #91a260;
    color: #181b10;
    font-size: 13px;
    font-weight: bold;
	  line-height: 38px;
    box-shadow: 0px 0px 8px transparent inset;
    right: 0px;
    border-right: 1px solid #677344;
    line-height: 2.75rem;
  }
  
  .user_toolbar > ul > li:hover {
   background: linear-gradient(to bottom, #97a964 0%, #8b9b5c 100%);
   text-shadow: 1px 1px #9db068;
  }
  
  .user_toolbar > ul > li::before {
    background: transparent;
  }
  
  .user_toolbar > ul > li::after {
    background: transparent;
  }
  
  .user_toolbar > ul > li:last-of-type {
	 border-right: 1px solid #677344;
  }

  .user_toolbar .button,
  .user_toolbar > ul > li > a,
  .big_button {
    text-transform: none!important;
  }
}
