* {
    font-family: "Open Sans";
}

body {
    margin: 0;
    padding: 0;
}

#hnmain {
    width: 100%;
}

td.default {
    border: 1px solid #d0d0d0;
    border-left: 11px solid #d0d0d0;
    padding: 0 20px 20px 20px;
    box-shadow: 0px 5px 22px -12px #000;
    word-break: keep-all;
}

td.default:hover,
td.default:focus,
td.default:active {
    border: 1px solid #606060;
    border-left: 11px solid #606060;
    background: #fff;
    transition: all 0.4s;
}

.c00, .c5a, .c73, .cbe, .c88, .c9c, .cae, .cdd, .cce {
    font-size: 1.15em;
    line-height: 24px;
}

.togg {
    font-size: 1.5em;
    position: relative;
    display: inline-block;
    margin: 0 0 0 10px;
}

.reply {
	border-top: 1px solid #e0e0e0;
    margin: 15px 0 0 0;
}

.reply a {
    display: block;
    padding: 4px 14px;
    background: #e2e2e2;
    float: right;
    text-decoration: none !important;
    text-transform: uppercase;
}

.votearrow {
    padding: 13px 12px 15px 12px;
    background-color: #e0e0e0;
    border: 1px solid #e0e0e0;
    color: #000;
    background-position: center center;
    box-sizing: border-box;
    position: relative;
    top: -4px;
}

.votearrow:hover {
    border: 1px solid #b0b0b0;
    box-shadow: 0px 3px 14px -5px #000;
}

.pagetop {
    background: #fff;
    display: inline-block;
    width: 100%;
    position: relative;
    margin: -13px -5px 5px -30px;
    padding: 10px;
    box-shadow: 0px 3px 14px -5px #000;
}

.pagetop a {
    display: inline-block;
    position: relative;
    font-family: Roboto;
    font-size: 1.3em;
    padding: 15px 5px;
    margin-top: 9px;
    border: 1px solid white;
}

.pagetop a:hover {
    background-color: #f0f0f0;
    border: 1px solid #d0d0d0;
    transition: all 0.2s;
}


.athing td:first-child {
    padding: 5px 0 0 0;
}

.athing td:last-child {
    width: 100%;
    vertical-align: middle;
    position: relative;
    top: -1px;
    padding-bottom: 9px;
}

.spacer {
    height: 25px !important;
}
.title a {
    font-family: Roboto;
    font-weight: bold;
    padding: 0 10px;
    font-size: 1.2em;
}

.athing td:last-child:hover {
    background: #fff;
    transition: all 0.2s;
}

.title a:visited {
	color: #ccc;
}


.subtext {
	font-size: 0.75em;
    padding: 0 0 0 10px;
}

.subtext a:last-child {
	font-weight: bold;
    padding: 15px;
}

.age a {
	font-weight: normal !important;
    padding: 0px !important;
}






































