css:
@-moz-document url-prefix("https://docs.google.com/document/d") {
    @media screen and (min-width: 1400px) {
        #docs-chrome {
            position: absolute !important;
            left: 0 !important;
            top: 0 !important;
            width: 260px !important;
            max-width: initial !important;
            z-index: 1000 !important;
            box-shadow: 0px 0px 5px #777 !important;
        }
        .docs-menubar, #docs-toolbar-wrapper, .docs-collapsable-toolbar, .goog-toolbar {
            width: 250px !important;
            max-width: initial !important;
            padding: 0 !important;
            margin: 0 !important;
            display: block !important;
            border: initial !important;
            background: #fff !important;
            white-space: initial !important;
        }
        #docs-toolbar-wrapper {
            margin: 10px 0 !important;
        }
        #docs-toolbar {
            left: 10px !important;
            line-height: 40px !important;
        }
        #docs-toolbar>div {
            margin: 0 !important;
        }
        .goog-toolbar-button, .menu-button, .goog-toolbar-combo-button, .goog-toolbar-menu-button, .docs-toolbar-menu-button, .goog-toolbar-select {
            cursor: pointer !important;
            display: inline-block !important;
            margin: 0 !important;
        }
        #printButton {
            margin-left: 10px !important;
        }
        .goog-toolbar-separator {
            opacity: 0 !important;
        }
        #docs-font-family {
            width: 130px !important;
        }
        #docs-font-family>div, #docs-font-family>div>div {
            width: 100% !important;
        }
        #docs-font-family .goog-toolbar-menu-button-caption {
            width: 100px !important;
        }
        #docs-header {
            max-height: initial !important;
            height: 125px !important;
        }
        .docs-titlebar-buttons {
            padding: 0 0 0 0;
            top: 63px !important;
            left: -40px !important;
            right: initial !important;
        }
        .docs-titlebar-badges {
            position: absolute !important;
            top: 65px !important;
            right: 10px !important;
        }
        #docs-notice {
            max-width: initial !important;
            width: 180px !important;
            top: -136px !important;
            left: 34px !important;
        }
        .docs-title-save-label-text {
            overflow: initial !important;
            white-space: normal !important;
            line-height: 11px !important;
            font-size: 11px !important;
        }
        #docs-omnibox-toolbar {
            display: none !important;
        }
        #docs-menubars {
            height: 66px !important;
            margin-top: 10px !important;
            position: relative !important;
            padding: 8px 0 10px 5px!important;
            box-sizing: border-box !important;
            border-top: 1px solid #ddd !important;
            border-bottom: 1px solid #ddd !important;
            background: #eee !important;
        }
        #docs-menubar {
            background: initial !important;
        }
        .menu-button {
            width: 45px !important;
            margin: 0 !important;
            float: none !important;
            display: inline-block !important;
        }
        #alignSeparator {
            display: block !important;
            height: 0 !important;
        }
        #docs-extensions-menu {
            width: 50px !important;
        }
        #docs-help-menu, #docs-screenreader-menu, #docs-debug-menu {
            display: none !important;
        }
        #docs-chrome {
            display: block !important;
            float: left !important;
        }
        .docs-title-outer {
            margin-left: 7px !important;
        }
        .docs-title-widget, .docs-title-input, .docs-title-input-label {
            max-width: 190px !important;
            width: 190px !important;
        }
        .docs-title-input-label {
            padding: 2px 5px !important;
        }
        #docs-chrome #gb {
            top: 103px !important;
            right: -10px !important
        }
        .docs-parent-collections-container-outer {
            clear: both !important;
        }
        #docs-bars {
            height: 100vh !important;
        }
        #docs-side-toolbar {
            position: relative !important;
        }
        .gb_ga {
            right: -104px !important;
        }
        #zoomSelect, #fontSizeSelect {
            top: 0 !important;
        }
        .goog-toolbar-menu-button-caption {
            margin-left: 5px !important;
        }
        .goog-toolbar-combo-button-input {
            padding-left: 5px !important;
        }
        .jfk-tooltip {
            display: none !important;
        }
        #docs-editor {
            background: #EEEEEE !important;
            width: 100vw !important;
        }
        #viewModeButton {
            position: fixed !important;
            bottom: 20px !important;
            left: 20px !important;
            display: none !important;
        }
        #viewModeButton {
            transform: rotate(-90deg) scale(1.5) !important;
        }
        .docs-butterbar-container-compact~*:not(#viewModeButton) {
            position: absolute !important;
            left: -300px !important;
        }
        .docs-image-effect-sidebar {
            height: 100vh !important;
            top: 0 !important;
            outline: none !important;
            box-shadow: 0px 0px 5px -2px #777 !important;
        }
        .docs-revisions-sidebar div {
            outline: none !important;
        }
        #docs-chrome-cover {
            height: 90px !important;
        }
        .docs-revisions-chromecover-titlebar-name-label {
            position: relative;
            top: -22px;
            color: #000;
        }
        .docs-revisions-chromecover-titlebar-button-action {
            position: absolute;
            top: 42px;
        }
        .docs-revisions-chromecover-titlebar-button-back {
            position: relative;
            top: -25px;
        }
        .docs-icon-arrow-back-white {
            filter: invert() !important;
        }
        #docs-toolbar-iterator-label {
            margin-left: 15px;
        }
        #docs-chrome div[aria-disabled="true"]:not(.goog-toolbar-separator) {
            height: 0 !important;
            width: 0 !important;
            overflow: hidden !important;
        }
        .docs-revisions-tile {
            cursor: pointer !important;
        }
        #moreButton {
            display: none !important;
        }
        .goog-toolbar-separator {
            display: none !important;
        }
    }
}
