css:
@-moz-document domain("boards.dangeru.us") {
hr {
    border: none;
    display: block;
    padding: 2px;
    background: #222;
}
img {
    width: 365px;
    height: auto;
    padding: 0px;
}
div#title {
    display: block;
    padding: 10px;
}
div#sitecorner {
    border-radius: 0px;
    border: none;
    border-bottom: 1px solid #333;
    background: #111;
    width: 600px;
}
div#redtext,
div#comment {
    line-height: 1;
    margin-top: -1px;
    border-bottom: 1px solid #333;
    word-wrap: break-word;
    padding: 0px 10px 4px 10px;
    display: block;
    background: #222;
}
br+div#comment,
br+div#redtext {
    padding-top: 6px;
    margin-top: -10px;
}
#title+div#comment,
#title+div#redtext {
    padding-top: 6px;
}
textarea,
input[type=text] {
    background: #222;
    border: 0;
    border-radius: 0;
    padding: 4px 0px;
    width: 100%;
    color: #fff;
    font-size: 18px;
    font-family: 'CyberpunkWaifus';
}
a,
input[type=submit] {
    cursor: pointer;
    margin-bottom: 4px;
    display: block;
    border: 1px solid #333;
    border-top: none;
    border-bottom: none;
    background: #151515;
}
a:hover,
input[type=submit]:hover {
    background: #222;
}
#pagecount_container > a,
#boardcontainer > a,
input[type=submit] {
    color: #fff;
    font-family: 'CyberpunkWaifus';
    font-size: 20px;
    display: inline-block;
    padding: 2px 10px 2px 10px;
}
}
