css:
@-moz-document domain(ap-pro.ru) {
.signatureView {
    margin-top: 7px;
}
div#feature_list ul li:first-child {
    margin-top: 4px;
}
.ap-inp_mo {
    border-radius: 0;
}
.ap-inp_cat {
    border-radius: 0;
}
.ap-inp_rat {
    border-radius: 0;
}
.ap-top_plvb {
    margin: 3px;
    border-radius: 2px;
}
.swchItemA {
	padding: 7px;
}
.swchItemDots {
    padding: 7px;
    background: #2d3338;
    cursor: default;
}
.swchItem {
	transition: .15s ease-in-out;
}
.catPages1 {
    padding: 15px 15px 25px 15px;
}
.block_zg {
    font-size: 18px;
    letter-spacing: 0.4px;
    line-height: 49px;
    height: 49px;
    padding: 0 15px 0 15px;
    font-stretch: condensed;
    text-shadow: none;
}
.gTableSubTop {
    font-size: 12px;
}
.ap-n_mp, .ap-n_mp a {
    color: #888 !important;
}
.anim {
    -webkit-transition: 0.15s ease-in-out;
    -moz-transition: 0.15s ease-in-out;
    -o-transition: 0.15s ease-in-out;
    -ms-transition: 0.15s ease-in-out;
    transition: 0.15s ease-in-out;
}
#tiptip_content {
    padding: 6px 12px;
    background-color: rgba(0, 0, 0, 0.8);
}
#tiptip_content {
    font-size: 12px;
    color: #fff;
    text-shadow: none;
    border: none;
    background-image: none;
    border-radius: 2px;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    box-shadow: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
}
#tiptip_content img {
    margin-top: 5px;
}
#tiptip_arrow {
    content: '';
    position: absolute;
    top: 0px;
    transform: rotate(-180deg);
    border: 7px solid transparent;
    border-top: 5px solid rgba(0, 0, 0, 0.8);
}
.ap-ava_gl {
    opacity: 0.8;
}
.switch {
    font-size: 11px;
    transition: .15s ease-in-out;
    padding: 0;
}
.switch:hover {
    background-color: #2A2A2A;
}
.switchActive {
    font-size: 11px;
    cursor: default;
}
.pagesInfo {
	padding: 0 10px;
    color: #BBB;
}
a.switchDigit:link, a.switchBack:link, a.switchNext:link {
    padding: 5px 8px;
}
.com_c {
    margin-top: 2px;
    padding-bottom: 10px;
}
ul#tabs li a {
    border-radius: 0;
}
.comment_avatar {
    background: none;
    border-radius: 50%;
    border: 1px solid #222;
    margin: 0 15px 0 0;
    padding: 0;
    width: 45px;
    height: 45px;
}
.com_p .grey, .grey a {
    color: #656565!important;
}
.com_p .fa-clock-o {
	margin-left: 8px;
}
.com_c .grey {
    margin-top: 5px;
}
.com_c .grey a:hover {
	text-decoration: underline;    
}
input[type="reset"], input[type="submit"], input[type="button"], button[type="button"] {
    border-radius: 1px;
    font-size: 12px;
    height: 30px;
    line-height: 30px;
    cursor: pointer;
    margin: 1px;
    padding: 0 15px;
}
#acform {
    width: 680px;
    margin: 0 auto!important;
}
.commFl {
    height: 100px;
    margin: 15px 0 0 0;
    border-radius: 2px;
}
#addcBut {
    margin: 15px 0 0 0;
}
.ap-mt_hk, .ap-combar {
    background: none repeat scroll 0 0 #111;
    border-top: 1px solid #171717;
    border-bottom: 1px solid #171717;
    border-right: 0;
    border-left: 0;
    border-radius: 2px;
    color: #888;
    font-weight: 600;
    padding: 10px 25px;
    font-size: 17px;
    margin: 15px 0;
}
.com-order-block {
    margin: -49px 0 0 593px;
    position: absolute;
    height: 24px;
}
#allEntries {
    width: 678px;
    margin: 0 auto;
}
.cell_com_os {
    margin-top: 10px;
}
.ap-mt_txt {
    margin: 0 25px 40px 25px;
    line-height: 1.48;  
}
.u-star-rating-12 {
    margin-right: 25px;
}
.ap-mt_txt + div + noindex {
    margin-left: 25px!important;
}
.ap-mt_txt + div + noindex + a + a + noindex {
    margin-left: 25px;
    display: block;
    margin-top: 3px;
    margin-bottom: 15px;
}
}
