css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(fanfiction.net) {

  /* link color */
a:visited {
    color: red;
}
a:hover {
    color: green;
}
  
  /* centering story, put margin, changing font and font size  */
  
div#storytext, div#storycontent {
  width: 75% !important;
  margin-left: auto !important;
  margin-right: auto !important;
  font-family: Comic Sans MS !important;
  font-size: 40px !important;
  line-height: 80px !important;
  }

  /* changin font and font-size to summary in story. In Belwe. Just cuz. If unavaible I suggest Comic Sans MS */
  
  div#profile_top {
    font-family: Belwe CN BT !important;
    font-size: 18px !important;
  }
  
  /*menù in Avangarde, st_inside is for the summary in author page, zmenu is the secondary menu (browse) top for the main menu (link, login, logout)*/
  div#st_inside, div#zmenu, div#top {
    font-family: AvantGarde Md BT !important;
      }
    div#st_inside {
      font-size: 20px !important;
   width: 90% !important;
  margin-left: 20px !important;
  margin-right: auto !important;
  }
  
  /*author bio in georgia, font size 16. If unavaible I suggest Comic Sans MS*/
  div#bio {
    
    font-family: AvantGarde Md BT !important;
    font-size: 16px !important;
  }
}
