css:
@-moz-document regexp(".*?\\.myjetbrains\\.com.*") {
body {
    background: #1e1e1e;
    color: #ccd3d6 !important;
}
a {
    color: #489dc7;
}
input {
    color: #ccd3d6 !important;
    background: #1D1E1F;
}
.text-copier {
    position: absolute;
    z-index: 1;
    top: 0;
    left: 0;
    cursor: pointer;
    width: 17px;
    height: 16px;
    filter: invert(75%);
}
.wiki-tbl-th{
    padding: 2px;
    font-weight: bold;
    border: 1px solid #898989;
    background-color: #333;
    color: #ccd3d6;
}
.search-panel__context-btn:hover{
	background: linear-gradient(to bottom, #1e1e1e 0%,#1e1e1e 100%);
}
.jt-panel-title{
	background: linear-gradient(to top, #333, #333) !important;
	color: #ccd3d6 !important;
}
.jt-dialog{
	background: linear-gradient(to top, #1e1e1e, #1e1e1e) !important;
	color: #ccd3d6 !important;
}
.newIssue{
    margin-top: 4em;
}
.search-panel__search-btn {
    background: #1e1e1e;
    border-left: 0;
}

.search-panel__search-ico{
    text-shadow: none;
}
.search-panel__search-btn:hover{
    background: #333;
}
.inputWrapper{
    background-color: #333
}
.search-panel__context-btn_disabled, .search-panel__context-btn_disabled:hover{
    color: #ccd3d6;
}
.attach-wrapper.add-comment__attachments.has-attachments {
    background-color: #333;
    color: #ccd3d6;
}
.jt-input, .ttrack-input {
    color: #ccd3d6;
}
/* For the "inset" look only */

html {
    overflow: auto;
}
body {
    position: absolute;
}
.comment-selected {
    background-color: #333;
}
.upload__drop-area {
    background-color: #000000;
    color: #eeeeee;
}
/* Let's get this party started */

::-webkit-scrollbar {
    width: 12px;
}
/* Track */

::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    -webkit-border-radius: 10px;
    border-radius: 10px;
}
/* Handle */

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 10px;
    border-radius: 10px;
    background: rgb(86, 86, 86);
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5);
}
.description_fsi {
    color: #ccd3d6 !important;
}
.issueId_fsi, a.issueId_fsi {
    color: #a2a2a2;
}
.agile:before, .smartui:before {
    background: #333;
    height: 100px;
}
.fsi-properties-container {
    background: #333;
}
.fsi-properties .fsi-property .attribute.disabled, .disabled *, .jt-fieldset-pair.jt-fs-pair-left label, .jt-fieldset-pair.jt-fs-pair-right label {
    color: #ccd3d6 !important;
}
a:visited {
    color: #489dc7;
}
.fsi-properties .fsi-property .attribute,
.ring-menu__item__i,
body .ring-link_pseudo {
    color: #489dc7 !important;
}
.fsi__sidebar__bottom_sticky {
    background: #252525;
}
.yt-action {
    color: #ccd3d6;
}
.add-comment .comment-textarea {
    background: #252525;
    color: #ccd3d6;
}
.jt-tabpanel-item .jt-panel-title {
    color: #489dc7 !important;
}
.jt-tabpanel-item.jt-panel-active, .jt-menu-button {
    background: #252525;
    color: #ccd3d6 !important;
}
.jt-panel-active div, .ring-tabs__btn__counter, .search-panel__search-btn {
    color: #ccd3d6 !important;
}
.search-panel-input .jt-input, .selectProject input {
    background: #252525 !important;
    color: #ccd3d6 !important;
}
.comboboxList, .jt-menu {
    background: #252525;
}
.jt-menu.li.a {
    color: #eeeeee;
}
.vert-list-item-counter, .vert-list-item-counter:before, .issues-wrapper, .issue-selected {
    background: #252525;
}
.vert-list-actions, .vert-list-item:hover {
    background: #333;
}
.issue {
    border: none;
}
.issue-selected {
    border: 1px solid #6ba6e1;
}
.issues-wrapper, .sidebar-toggler {
    border: none;
}
.sidebar-toggler:hover {
    background: #333;
}
.ring-btn, .btn, .jt-button {
    color: #ccd3d6;
    box-shadow: none;
    background: #252525 none;
}
.ring-btn:hover, .ring-btn.ring-btn_hover {
    background: #252525 none;
}
.issues-nav-info, .ring-footer {
    color: #ccd3d6;
}
.combobox,.combobox input, .jt-input[type=text], .jt-input[type=password] {
    background: #252525;
}
.ring-input, .jt-input, .ring-query-assist__letter {
    background-color: #252525;
    color: #ccd3d6;
}
.table th, .table-light th {
    background: #252525;
    color: #ccd3d6;
}
.search-panel, .selectProject, .search-center .panel, .search-panel__context-btn, .suggest {
    background: #333;
    border-color: #333 !important;
    border-radius: 0;
    -webkit-box-shadow: 0 2px 0 #ccd3d6;
    box-shadow: none;
}
.suggest ul .separator .description, .suggest div.comment, .toolbar-unpined {
    background-color: #252525;
    color: #ccd3d6;
}
.fsi-top-toolbar.toolbar-unpined {
    -webkit-box-shadow: none;
    box-shadow: none;
}
.ttrack-item-selected, .ttrack-item-selected:first-child, .history-item-title {
    background: #252525;
}
.yt-vcs-changes__toolbar__items {
    background-color: #333;
    border: 1pt solid #eeeeee;
}
.wikicode {
    background: #252525;
    color: #ccd3d6;
}
.pln {
    color: #ccd3d6;
}
.typ {
    color: #26b345;
}
.comment-change-wrapper {
    background: #252525;
}
.comment-selected .comment-change-wrapper {
    background: #1e1e1e;
}
.diff-del {
    background-color: #840000;
}
.diff-ins {
    background-color: #448200;
}
.calendar, .calendar .title, .calendar .navigation {
    color: #ccd3d6;
    background-color: #1e1e1e;
}
.calendar .button .push, .calendar .weekend {
    background-color: #333;
}
.jt-menu-item-active, .jt-menu li a, .jt-menu, .comboboxList li.active, .comboboxList li, .suggest ul li.current {
    background: #1e1e1e;
    color: #ccd3d6;
}
.comboboxList li.active *, .comboboxList li:hover * {
    color: #ccd3d6 !important;
}
.jt-menu li a, .comboboxList {
    background-color: #1e1e1e !important;
    color: #ccd3d6 !important;
}
.jt-menu li a:hover, .comboboxList li.active, .comboboxList li:hover {
    background-color: #333 !important;
    color: #ccd3d6 !important;
}
.tag-list_issues, .tag-list_edit {
    background-color: #1e1e1e;
}
.jt-input[type=text], .jt-input[type=password] {
    border-color: #333;
}
.search-center .panel,
.selectProject {
    border-color: #252525 !important;
}
.selectProject .inputWrapper,
.search-center .panel, .selectProject {
    background: #252525;
    box-shadow: none;
}
.tag {
    color: #007cff;
}
.atn {
    color: #bd3bbd;
}
.kwd {
    color: #007cff;
}
}
