#search input[type="text"] {
    display: inline-block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
    height: 37px;
    padding: 5px 85px 6px 12px;
    font-size: 0.875em;
    line-height: 1;
    color: #292f33;
    border: 1px solid #e1e8ed;
    border-radius: 21px;
    transition: all .2s ease-in-out;
    background: #fff url(http://i.imgur.com/sk19EIJ.png) right no-repeat;
}

