css:
@-moz-document domain("fitgirl-repacks.site") {
a,
input {
	-webkit-transition: all 0.1s ease-out 0s !important;
	-moz-transition: all 0.1s ease-out 0s !important;
	-o-transition: all 0.1s ease-out 0s !important;
	-ms-transition: all 0.1s ease-out 0s !important;
	transition: all 0.1s ease-out 0s !important;
}
::-moz-selection {
	background: #C18888 !important;
}
::selection {
	background: #C18888 !important;
}
*::-moz-placeholder {
	color: #FFF;
}
.secondary-navigation li:hover > a,
.secondary-navigation li.focus > a {
	background-color: #1C1C1C;
	color: #fff;
}
.site-footer,
.site-content .entry-header,
.site::before,
.site-content .entry-content,
.site-content .entry-summary,
.page-content,
body.custom-background,
.site-header,
input,
textarea,
button,
.button,
input[type="button"],
input[type="reset"],
input[type="submit"] {
	background-color: #303030;
}
.site-navigation li,
.secondary-navigation li {
	border-top: 1px solid rgb(29, 29, 29);
}
.site-navigation li:first-child,
.secondary-navigation li:first-child {
	border-top: 0;
}
.secondary-navigation {
	border-bottom: 1px solid rgb(29, 29, 29);
}
/*Replace a lot of green with pink*/

.search-toggle,
.search-toggle.active,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a,
.entry-meta .tag-links a:hover,
.widget input[type="button"]:hover,
.widget input[type="button"]:focus,
.widget input[type="reset"]:hover,
.widget input[type="reset"]:focus,
.widget input[type="submit"]:hover,
.widget input[type="submit"]:focus,
button:hover,
button:focus,
.button:hover,
.button:focus,
input[type="button"]:hover,
input[type="button"]:focus,
input[type="reset"]:hover,
input[type="reset"]:focus,
input[type="submit"]:hover,
input[type="submit"]:focus,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a,
.site-navigation .current_page_item > a,
.site-navigation .current_page_ancestor > a,
.site-navigation .current-menu-item > a,
.site-navigation .current-menu-ancestor > a,
.entry-meta .tag-links a:hover::before {
	background-color: #C55 !important;
}
.primary-sidebar .widget input[type="submit"]:hover {
	color: #fff !important;
	background: #C55 !important;
}
.site-title a,
.footer-sidebar .widget h1,
.primary-sidebar .widget h1 {
	color: #C55 !important;
}
.entry-content h3 span {
	color: #C55 !important;
}
.entry-content h3 span span {
	color: #CCA !important;
}
.entry-content p span {
	color: #ACA !important;
}
.entry-content p span span a {
	color: #C55 !important;
}
.search-field {
	background-color: #333 !important;
}
#main,
.main-content,
.content-area,
.content-sidebar {
	background-color: #222222 !important;
}
.su-spoiler-style-fancy > .su-spoiler-title {
	border-radius: 10px 10px 0 0 !important;
	border-bottom: 1px solid #505050 !important;
	background: #222 !important;
}
.su-spoiler-style-fancy {
	border: none;
	background: #3E3E3E !important;
	color: #8C8C8C !important;
}
table,
th,
td {
	border: 1px solid rgb(34, 34, 34);
}
.site-content .entry-meta {
	background-color: transparent;
}
.cat-links a:hover,
.entry-meta a:hover,
a:active,
a:hover,
.site-info a:hover,
.entry-title a:hover,
.widget a:hover,
.comment-author a:hover,
.comment-list .pingback a:hover,
.comment-list .trackback a:hover,
.comment-metadata a:hover,
.post-navigation a:hover,
.image-navigation a:hover {
	color: #C55 !important;
}
body,
button,
input,
select,
.entry-title a,
textarea,
.cat-links a,
.entry-meta a,
a {
	color: #8C8C8C !important;
}
.widget button,
.widget .button,
.widget input[type="button"],
.widget input[type="reset"],
.widget input[type="submit"],
.widget button,
.widget .button,
.widget input[type="button"],
.widget input[type="reset"],
.widget input[type="submit"] {
	background-color: rgba(255, 255, 255, 0.1);
}
html body #page header .header-main .search-toggle:hover,
.search-toggle.active {
	background-color: #C55 !important;
}
.site-navigation .current_page_item > a,
.site-navigation .current_page_ancestor > a,
.site-navigation .current-menu-item > a,
.site-navigation .current-menu-ancestor > a,
.primary-navigation li:hover > a,
.primary-navigation li.focus > a {
	color: #222;
}
.paging-navigation .page-numbers.current {
	border-top: 5px solid #C55;
}
.paging-navigation a:hover {
	border-top: 5px solid #C55;
	color: #8C8C8C;
}
#main {
	background: black;
	padding-top: 48px !important;
}
.site {
	max-width: inherit !important;
}
.site-header {
	position: fixed !important;
	max-width: inherit !important;
	box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.3) !important;
}
article > header {
	padding-top: 20px !important;
}
.primary-navigation li:hover > a,
.primary-navigation li.focus > a {
	background-color: #C55 !important;
	color: #fff !important;
}
.search-toggle {
	margin-top: 1px;
	background-color: transparent !important;
	-webkit-transition: all 0.1s ease-out 0s !important;
	-moz-transition: all 0.1s ease-out 0s !important;
	-o-transition: all 0.1s ease-out 0s !important;
	-ms-transition: all 0.1s ease-out 0s !important;
	transition: all 0.1s ease-out 0s !important;
}
.search-toggle:before {
	color: #8C8C8C;
}
html body #page header .header-main .active {
	height: 49px;
}
html body #page header .header-main .search-toggle:hover:before,
html body #page header .header-main .active:before {
	color: #fff !important;
}
* {
	-webkit-font-smoothing: antialiased !important;
	-webkit-text-shadow: rgba(0, 0, 0, .01) 0 0 1px !important;
	text-shadow: rgba(0, 0, 0, .01) 0 0 1px !important;
}
.su-spoiler-style-fancy {
	background-color: inherit !important;
}
article > header {
	border-top-left-radius: 10px !important;
	border-top-right-radius: 10px !important;
}
.entry-content {
	border-bottom-left-radius: 10px !important;
	border-bottom-right-radius: 10px !important;
}
.su-spoiler-content {
	background-color: #363636 !important;
	padding-top: 10px !important;
	border-radius: 0 0 10px 10px !important;
	-webkit-transition: all 0.3s ease-out 0s !important;
	-moz-transition: all 0.3s ease-out 0s !important;
	-o-transition: all 0.3s ease-out 0s !important;
	-ms-transition: all 0.3s ease-out 0s !important;
	transition: all 0.3s ease-out 0s !important;
}
.su-spoiler-style-fancy.su-spoiler-closed > .su-spoiler-title {
	border-radius: 10px !important;
}
.su-spoiler-style-fancy {
	border: 0px !important;
}
img {
	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.3), 0 6px 20px 0 rgba(0, 0, 0, 0.29) !important;
}
.paging-navigation {
	border-top: 5px solid #303030;
}
.paging-navigation .page-numbers {
	color: #444;
}
.paging-navigation .page-numbers:hover {
	border-top: 5px solid #666;
}
.paging-navigation .page-numbers.current {
	border-top: 5px solid #C55;
}
#secondary {
	z-index: 999 !important;
	margin: 0 !important;
	position: fixed !important;
	color: #8C8C8C;
}
.search-box {
	display: block !important;
	border-bottom-left-radius: 10px;
	background-color: #C55;
}
.search-box-wrapper.hide {
	visibility: hidden;
	opacity: 0;
}
.search-box-wrapper {
	visibility: visible;
	opacity: 1;
	-webkit-transition: all 0.3s ease-out 0s !important;
	-moz-transition: all 0.3s ease-out 0s !important;
	-o-transition: all 0.3s ease-out 0s !important;
	-ms-transition: all 0.3s ease-out 0s !important;
	transition: all 0.3s ease-out 0s !important;
}
.search-box-wrapper {
	top: 50px;
	width: auto;
}
.su-box {
	color: #9c9c9c !important;
	border-color: #222 !important;
}
.su-box-title{
	color: #9c9c9c !important;
    background-color: #222 !important;
}
.su-box-content{
	color: #9c9c9c !important;
	background-color: #303030 !important;
}
}

@-moz-document domain("opinionstage.com") {
* {
	-webkit-transition: all 0.5s ease 0s;
	-moz-transition: all 0.5s ease 0s;
	-o-transition: all 0.5s ease 0s;
	-ms-transition: all 0.5s ease 0s;
	transition: all 0.5s ease 0s;
}
html body #os-wrapper #os-body.thin-border {
	border-width: 0 !important;
	border-top: 1px solid rgb(80, 80, 80) !important;
}
html body #os-wrapper #os-body {
	background-color: transparent !important;
}
html body label {
	color: #C55 !important;
}
html body #os-wrapper h1,
#os-wrapper h2 {
	color: #8C8C8C !important;
}
html body #os-wrapper .side-votes.show-results .side-wrapper .side-fill-wrapper .side-fill {
	background-color: #222 !important;
}
html body #os-wrapper .side-votes .os-side {
	border-left: 1px solid rgba(18, 32, 53, 0.25) !important;
	border-right: 1px solid rgba(18, 32, 53, 0.25) !important;
}
html body #os-wrapper .side-votes .os-side:nth-child(2) {
	border-top: 1px solid rgba(0, 0, 0, 0) !important;
}
html body #os-wrapper .os-side {
	border-top: 1px rgba(18, 32, 53, 0.25) !important;
	background-color: #303030 !important;
	color: #C55 !important;
}
html body #os-wrapper .side-votes.show-results .side-wrapper .user-selected {
	background-color: #C55 !important;
}
html body #os-wrapper .side-votes .vote-bar {
	background-color: #303030;
	border: 1px solid rgba(18, 32, 53, 0.25) !important;
	color: #8C8C8C !important;
}
html body #os-wrapper .side-votes .sides-wrapper {
	border-bottom: 0 !important;
}
html body #os-wrapper .os-link-wrapper {
	border: 0 !important;
}
html body #os-wrapper .os-social-bar .logo-area .box.toggle-boxes {
	border-top: solid 1px transparent;
	border-bottom: solid 1px transparent;
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.twitter {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.facebook {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.gplus {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.pinterest {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.tumblr {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area .box.reddit {
	background-color: #555 !important;
}
html body #os-wrapper .os-social-bar .icons-area div a .os-icon,
html body #os-wrapper .os-social-bar .icons-area div a .label {
	color: #BFBFBF !important;
}
html body #os-wrapper .os-social-bar .icons-area div a:hover .os-icon,
html body #os-wrapper .os-social-bar .icons-area div a:hover .label {
	color: #C55 !important;
}
html body #os-wrapper .os-social-bar .logo-area .box.toggle-boxes .os-icon {
	color: #BFBFBF !important;
}
html body #os-wrapper .os-social-bar .logo-area .box.toggle-boxes:hover .os-icon {
	color: #C55 !important;
}
}
