css:
@-moz-document url("https://web.whatsapp.com/") {
    body {
        background-image: url(http://pre09.deviantart.net/c179/th/pre/f/2015/059/5/e/5e37fc8829ba87daa069f7af831fc12f-d8jwyjc.jpg);
        background-size: cover;
        background-position: center;
    }
    .app-wrapper::before {
        display: none;
    }
    .drawer-container-right,
    .pane-chat,
    .pane-intro {
        background-image: url(http://orig00.deviantart.net/cb03/f/2013/158/d/c/fairytale_garden_by_rosel_d-d683lwg.jpg);
        background-color: rgba(0, 0, 0, 0.0);
        background-position: center;
    }
    .pane-chat-tile {
        display: none;
    }
    .app {
        background-color: rgba(0, 0, 0, 0.4);
        width: 100%;
        height: 100%;
        top: 0;
        border-radius: 0px;
    }
    .pane-chat,
    .pane-intro {
        width: 80%;
    }
    .drawer-header {
        background-color: #f7b2e6;
    }
    .pane-intro:after {
        border-top: 6px solid #000000;
    }
    .app,
    .app.three {
        width: 100%;
    }
    .drawer-container-panel,
    .pane-info {
        width: 385px;
        left: 80%;
    }
    .three .pane-chat {
        width: 59.8%;
    }
    .intro-title {
        color: #fff;
        font-size: 36px;
        margin-top: 38px;
        content: "";
    }
    .intro-text {
        font-size: 14px;
        color: #fff;
    }
    .intro-image {
        width: 356px;
        height: 355px;
        background-image: url("http://i.imgur.com/h96Go9K.png");
    }
    #wrapper:before {
        display: none;
    }
    .app-wrapper::after {
        position: fixed;
        z-index: -1;
        background-color: #ffffff;
        width: 0%;
        height: 127px;
        content: '';
        top: 0;
        left: 0;
    }
    .pane-intro:after {
        content: '';
        position: absolute;
        bottom: 0;
        left: 0;
        border-top: 6px solid #58e870;
        width: 0%;
        height: 0;
    }
}
