css:
@-moz-document domain("discordapp.com")
{

/*Specify font for entire page*/
* {font-family: Muli !important;/*font-size: 1em;*/}   
 
/*Zoom chat to %*/     
.chat {zoom:90%;}
 
/*Hide "Download app" button*/
.guilds-wrapper .btn-download-apps {display: none;}            
 
/*Zoom server list to %*/
.guilds-wrapper {zoom:60%;}
 
/*Display timestamp to the right*/
.timestamp {float: right;}                                                     
 
/*Collapse channel list, display on hover*/
.channels-wrap {
   width: 10px !important;             
   -webkit-transition: width 1s !important;
   transition: width 0.5s !important;
   -webkit-transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
   transition-timing-function: cubic-bezier(0.0, 0.0, 0.2, 1) !important;
}
.channels-wrap:hover {width: 250px !important;}
}
