css:
@-moz-document domain("magister.net") {
* {
    font-family: arial;
}
.splash {
    background: none;
}
.podium {
    top: 50px;
    right: 500px;
    position: inherit;
    z-index: 50;
}
.menu {
    z-index: 99;
    background-color: #323232;
}
.menu a:hover,
.menu .navTrigger:hover {
    background-color: #004d72;
}
.menu > ul {
    margin-top: 32px;
    border-top: 8px solid #ffffff;
    clear: left;
}
.menu ul ul {
    border: 1px solid #434343;
}
.menu a {
    position: relative;
    padding-left: 0;
    display: block;
    height: 44px;
    line-height: 44px;
    color: #ffffff;
    overflow: hidden;
    border-bottom: 0px solid #666666;
}
.menu .navTrigger {
    display: block;
    float: left;
    width: 100%;
    height: 48px;
    cursor: pointer;
    border-bottom: 0px solid #666666;
}
.main {
    position: relative;
    float: left;
    padding: 48px 0 0 0;
    width: 100%;
    height: 100%;
}
.widget .list li:hover,
.widget .list li.active {
    background-color: #e8e8e8;
}
div.image-carrousel div {
    background-image: None !important;
}
div.image-carrousel div.image1 {
    height: 0vh;
    max-height: 0vh;
    background-size: cover;
    background-position: center center;
    position: absolute;
    left: 0;
    top: 0;
    width: 0%;
    visibility: hidden;
    z-index: 0;
}
div.image-carrousel div.image2 {
    background-image: None;
    visibility: hidden;
    z-index: 0;
}
.master-view {
    padding-left: 43px;
}
.head-bar {
    position: absolute;
    top: 0px;
    right: 267px;
    left: 0;
    z-index: 680;
    padding-left: 8px;
    height: 48px;
    line-height: 46px;
    color: #ffffff;
    border-bottom: none;
    background-color: #0096db;
    font-size: 14px;
}
#berichten-container {
    padding-right: 51px;
}
.container {
    position: relative;
    height: 100%;
    padding-right: 293px;
}
#vandaag-container {
    padding-right: 0px;
}
#instellingen-container {
    padding-right: 0px;
}
#profiel-container {
    padding-right: 0px;
}
.widget {
    padding: 0px 0px 0 0;
    font-family: tahoma, sans-serif;
    font-size: 11px;
}
#vandaag-container div.content-container {
    padding-left: 0px;
    padding-right: 0px;
}
.content-container {
    z-index: 98;
}
#agenda-section .content-container,
#examendetail-container .content-container {
    padding-left: 0px;
}
.master-view.nav-toggle {
    padding-left: 186px;
}
.sidecolumn .head-bar {
    right: 294px;
}
body {
    margin: 0;
    padding: 0;
    height: 100%;
    text-align: center;
    font-family: arial;
    font-size: 13px;
}
aside .widget {
    padding-right: 2px;
}
aside {
    position: absolute;
    top: 0px;
    bottom: 0;
    right: 0;
    padding-right: 44px;
    padding-top: 2px;
    width: 294px;
    z-index: 100;
    overflow: hidden;
}
.aside-toggle.container .head-bar {
    right: 270px;
}
.sidecolumn .head-bar {
    right: 270px;
}
aside .sheets .tabsheet {
    padding: 0 0 2px 2px;
}
aside .head-bar {
    border: none;
    width: 294px;
    position: absolute;
    left: 8px !important;
    top: 0;
    text-align: left;
}
aside .tabs li {
    position: relative;
    float: left;
    margin-right: 0px;
    min-width: 134px;
    height: 42px;
    line-height: 44px;
    font-size: 13px;
}
aside .sheets {
    position: relative;
    height: 100%;
    padding-top: 47px;
    width: 100%;
    overflow-y: auto;
    overflow-x: hidden;
}
.block {
    position: relative;
    border: 1px solid #efefef;
    background-color: #ffffff;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
}
.header .header-user a,
.header .user a {
    padding: 0 0px;
    padding-top: 0px;
    padding-right: 0px;
    padding-bottom: 0px;
    padding-left: 15px;
}
.header {
    z-index: 101;
    height: 48px;
    background-color: #0096db;
}
.header > div + div {
    position: relative;
    float: right;
    border-left: 1px solid #59bbe8;
}
#studiewijzer-container div.studiewijzer-list > ul > li > a > span {
    padding-left: 8px;
    float: left;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    border-right: 1px solid #e8e8e8;
}
.header .logo {
    float: left;
    margin-left: 6px;
    font-size: 18px;
    line-height: 48px;
    color: #ffffff;
    border: none;
    font-family: "droid_sansregular", arial, helvetica, sans-serif;
}
.sm-grid.k-grid .k-grid-content tr.k-state-selected,
.sm-grid.k-grid .k-grid-content tr.k-state-selected.k-state-focused,
.sm-grid.k-grid .k-grid-content tr.k-state-selected .k-state-focused {
    cursor: pointer;
    background-color: #eeeeee;
}
.sm-grid.k-grid .k-grid-content tr:hover {
    background: #e8e8e8;
}
.header .header-user:hover,
.header .user:hover {
    background-color: #005c86;
}
.head-bar div > a:hover {
    background: #005c86;
}
.header div.logoff:hover {
    background-color: #005c86;
}
/* login form */

.login-dialog:before,
.change-password-dialog.no-preview:before {
    position: relative;
    float: left;
    width: 0px;
    height: 0px;
    background: transparent no-repeat top left;
    -moz-box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
    -webkit-box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
    box-shadow: 1px 0 5px -1px rgba(0, 0, 0, 0.7);
    content: '';
}
.login-dialog .content-pane,
.change-password-dialog.no-preview .content-pane {
    padding-left: 0px;
}
.dialog {
    top: 50%;
    left: 50%;
    border: 0px solid #cccccc;
}
.account-dialog {
    z-index: 1;
    margin-top: -175px;
    margin-left: -200px;
    width: 400px;
    height: 350px;
}
.dialog .title {
    font-family: arial;
}
.checkbox-input input[type="checkbox"]:checked + label span:after {
    color: #2aade8;
}
.dialog-overlay:before {
    background-color: #080c11;
}
.account-dialog__message {
    display: none;
}
.account-dialog .content-pane {
    padding-left: 0px;
}
}
