css:
@-moz-document domain(backpack.tf) {

body {
    font-family: "Roboto", sans-serif
}
body.app-440 {
    background-image: none;
    background-color: #353434
}
a {
    color: #636363
}
a:hover,
a:focus {
    color: #7a7575
}
.app-440 .price-change .price-new {
    background-color: #636363
}
.progress-bar {
    background-color: #636363
}
.alert-info {
    background-color: #f7d9d9;
    border-color: #f1bcbc;
    color: #8f3131
}
.alert-info .alert-link {
    color: #722626
}
.app-440 .incentive-list .incentive header {
    background-color: #636363
}
/*---------------*/
/* NOT DISPLAYED */
/*---------------*/

.app-440 .badge {
    display: none
}
.app-440 .navbar .nav > li > a {
    text-shadow: none
}
.app-440 .row-media > .col-md-5 {
    display: none
}
.app-440 .col-md-4 > .panel-default.panel:nth-of-type(1) > .panel-body {
    display: none
}
.app-440 #media-side {
    display: none !important
}
/*---------------------------*/
/* BUTTONS , PANELS & NAVBAR */
/*---------------------------*/

.app-440 .navbar {
    border: none;
    box-shadow: none
}
.app-440 .nav .dropdown-toggle .caret {
    border-top-color: #636363;
    border-bottom-color: #636363
}
.app-440 .site-search-dropdown li.header {
    border-bottom-color: #636363;
    background-color: #636363
}
.app-440 .dropdown-menu li > a:hover {
    background-color: #636363
}
.app-440 .dropdown .dropdown-menu .dropdown-header {
    padding: 5px 10px;
}
.app-440 .navbar-nav > li > .dropdown-menu {
    margin-top: 2px;
    border: none
}
.app-440 .btn.btn-primary {
    background-color: #636363;
    border-color: #636363
}
.app-440 .btn.btn-primary:hover,
.app-440 .btn.btn-primary:focus,
.app-440 .btn.btn-primary.focus,
.app-440 .btn.btn-primary:active,
.app-440 .btn.btn-primary.active,
.open > .app-440 .btn.btn-primary.dropdown-toggle {
    background-color: #636363;
    border-color: #adadad
}
.app-440 .navbar {
    border: none;
    box-shadow: none
}
.app-440 .navbar,
.app-440 .navbar-header {
    background: none;
    background-color: #636363
}
.app-440 .navbar-avatar {
    border-color: #636363
}
.app-440 .modal-header,
.app-440 .panel-main > .panel-heading {
    background: none;
    background-color: #636363;
    box-shadow: none !important;
    border-color: #636363;
    max-height: 35px;
    padding: 5px
}
.app-440 .nav-pills > li.active > a,
.app-440 .nav-pills > li.active > a:hover,
.app-440 .nav-pills > li.active > a:focus {
    background-color: #636363;
    border-color: #636363
}
a.btn.btn-default {
    height: 15px;
}
.app-440 .nav-pills > li > a {
    color: #636363
}
.app-440 .panel-body {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    border: none
}
.app-440 .popover .popover-title {
    background: #636363;
    box-shadow: none;
}
/*-------------------------------*/
/* BORDERS , PADDING AND MARGINS */
/*-------------------------------*/

.app-440 .panel-main {
    border: none
}
.app-440 .panel {
    border: none;
    box-shadow: none
}
.app-440 .index-latest-changes {
    padding-bottom: 15px;
    padding-left: 15px;
    padding-right: 15px;
    padding-top: 15px
}
.app-440 .top-prices {
    margin-bottom: 5px;
    margin-left: 160px;
    margin-top: 50px
}
.app-440 .row-media > .col-md-7 {
    width: 610px;
    margin-left: 170px
}
.app-440 .row .padded {
    padding: 5px
}
.app-440 .inventory .page-number {
    margin-bottom: 15px;
    margin-left: 440px;
    margin-top: 10px
}
.app-440 .panel-main .table tbody td,
.panel-main .table tbody th {
    background: none
}
.app-440 #unusual-pricelist {
    margin-bottom: 10px;
    margin-left: 15px;
    margin-top: 20px
}
#backpack-history-graph {
    height: 110px;
}
#pricelist-pagination-container {
    background-color: #c7c7c7;
}
#pricelistContainer {
    background-color: #c7c7c7;
}
a.btn.btn-danger.btn-xs {
    display: none;
}
a.btn.btn-default {
    height: 10px;
    width: 10px;
}
a.btn.btn-default.btn-xs {
    font-size: 10px;
    font-weight: bold;
    height: 28px;
    width: 77px;
}
a.btn.btn-panel.btn-csgo {
    display: none;
}
a.btn.btn-panel.btn-dota2 {
    display: none;
}
a.btn.btn-panel.btn-tf2 {
    display: none;
}
a.btn.btn-primary.btn-xs {
    display: none;
}
a.btn.btn-xs.btn-bottom.btn-warning.listing-report {
    display: none;
}
a.search-crumb {
    font-size: 12px;
}
a.user-link.premium {
    font-size: 13px;
    font-weight: bold;
}
div.col-md-4.knob-set {
    background-color: #c7c7c7;
}
div.footer-block.blurb {
    display: none;
}
div.footer-block.copyright {
    display: none;
}
div.footer-block.social {
    display: none;
}
div.friend {
    background-color: #c7c7c7;
}
div.highcharts-container {
    height: 110px;
}
div.highcharts-container svg {
    background-color: #c7c7c7;
    height: 110px;
}
div.index-classifieds {
    background-color: #c7c7c7;
}
div.information {
    background-color: #c7c7c7;
}
div.inventory {
    background-color: #c7c7c7;
}
div.padded {
    background-color: #c7c7c7;
}
div.panel-body {
    background-color: #c7c7c7;
}
div.panel-body.padded {
    background-color: #c7c7c7;
}
div.panel-heading {
    height: 28px;
}
div.popover-content {
    background-color: #c7c7c7;
}
div.profile {
    background-color: #c7c7c7;
}
div.quote-box p {
    font-size: 9.5px;
}
div.stats-body {
    background-color: #c7c7c7;
    border-color: #ffffff;
    border-style: none;
    font-weight: bold;
    text-transform: capitalize;
}
div.stats-body h2 {
    display: none;
}
div.stats-breadcrumbs {
    display: none;
}
div.stats-header {
    display: none;
}
div.stats-subheader {
    display: none;
}
div.suggestion.sg-base {
    background-color: #c7c7c7;
}
div.well.well-sm {
    display: none;
}
form.filter-list {
    background-color: #c7c7c7;
    border-style: none;
    height: 111px;
}
li small a {
    color: #8c8c8c;
    font-weight: bold;
}
main.container nav {
    padding-left: 50px;
}
rect.highcharts-background {
    background-color: #c7c7c7;
    height: 111px;
    padding-bottom: 30px;
}
ul.media-list.padded {
    background-color: #c7c7c7;
}
ul.stats li small {
    font-variant: small-caps;
    font-weight: bold;
    padding-bottom: 1px;
    text-transform: capitalize;
 }
}
