css:
@-moz-document url-prefix("https://ficbook.net/") {

/*интерфейс*/ body {
  background-color: #490005 !important;
  color:#000000 !important;
  font-family:cursive;
}
/*не нравится*/.disliked-parameter-link {
  background-color: rgba(0, 0, 255, 0) !important;
  color: #F8173E !important;
  /*text-decoration:line-through; /*зачеркнутый текст*/
}
/*нравится*/.liked-parameter-link {
  background-color: rgba(0, 0, 255, 0) !important;
  color: #009900 !important;
}
/*края страницы*/.container {
    margin-right: auto;
    margin-left: auto;
    padding-left: 0px !important;
    padding-right: 0px !important;
}  
/*низ страницы*/#footer {
  background-color: rgba(0, 0, 255, 0) !important;
}
#footer .footer-nav {
    width: 100%;
    font-size: 100% !important;
} 
/*низ страницы.иконка "к началу страницы"*/#toTop {
    color: #009900 !important;
    opacity: .2;
    position: fixed;
    cursor: pointer;
    background: #4f1700;
    text-decoration: none;
    text-align: center;
    padding-top: 4px;
    right: 20px !important;
    bottom: 0%;
    width: 92%;
    height: 40px;
    font-size: 32px;
    border-radius: 50%;
    transition: opacity .3s ease-in-out;
}  
/*категории и жанры в колонках*/.columned {-moz-column-count: 5;}
/*список избранных авторов в колонках*/div.author_list {-moz-column-count: 2;}
/*строка с вкладками "Избранные авторы"*/
.rkl {margin-bottom: 1px; margin-top: 1px;}
.content-section {max-width: 1800px !important;}
.nav-info {margin-bottom: 1px !important;}
/*логин бар*/ .header-t .dropdown.profile-holder button[data-toggle="dropdown"] .text {
  color: #ffff00;
  float: left;
  font-family:cursive !important;
  font-size: 24px;
  line-height: 24px;
  margin-bottom: 0;
  margin-left: 50px;
  margin-right: 10px;
  margin-top: 5px;
  text-decoration-color: -moz-use-text-color;
  text-decoration-line: underline;
  text-decoration-style: solid;
  z-index:10000!important;
}
/*лента избранных*/ #notifications {
  line-height: auto !important;/*расстояние между значками*/
  position: fixed !important;
  text-align: left !important;
  z-index: 1000!important;
}
div.tooltip-inner {  /*расстояние ...*/
  top: auto !important;
  left: auto !important;
  right: auto !important;
  width: auto !important;
  height: auto !important;
}
#notifications .top-notifications li {
  /*display: inline-block;*/
  width: 0px !important;
  height: 0px !important;
  margin-bottom: 0px !important;
  margin-right: 0px !important;
  margin-left: 15px !important;
  margin-top: 0px !important;
  padding-top: 0px !important;
  padding-bottom: 0px !important;
  padding-left: 0px !important;
  padding-right: 0px !important;
}  
#notifications .top-notifications li a {
    text-decoration: none;
    font-size: 18px;
  color: #BFFF00 !important;
  width: 0px !important;
  height: 0px !important;
}
  ul, menu, dir {
    margin-block-end: 0em;
    margin-block-start: 0em;
    padding-inline-start: 0px !important;
}
/*printfic начало*/ @-moz-document url-prefix("https://ficbook.net/printfic/") {
body {
  background-color: #FFEBCD !important;
  color:#000000 !important;
  font-family:cursive;
  font-size:100%;
}
} /*printfic конец*/
}
