css:
@-moz-document domain("www.itvonline.nl") {
#footer,
#navigation,
div.info,
.loader,
.tabs {
    display: none !important;
}
body {
    background: #111;
}
#channels-top-list-wrapper,
#video-controls,
.channel-details {
    color: #bbb !important;
    background: #333 !important;
    margin: auto;
}
a,
.time-range,
.time1,
.time2,
span.title{
    color: #aaa !important;
}
.box-top-container .box-content, .ui-widget-content {
	border: none;
    background: none;
}
}
