css:
@-moz-document domain("mangapedia.fr") {
  
    body {
        color: #FFFFFF;
    }
#mainContent {
    background-color: #212121;
}
  #banner {
    background-color: #424242;
}
  .infoMessage {
    background-color: #424242;
    box-shadow: 0px 0px 10px #000000;
      border: none;
}
  table th {
    background-color: #424242;
}
 #mainChaptersLatest > thead:nth-child(1) > tr:nth-child(1) > th:nth-child(1) {
    box-shadow: 0px 0px 10px #000000;
}
  table tbody tr:hover {
    border: none;
    background-color: #424242;

}
    #mainChaptersBodyList > tr {
            box-shadow: 0px 0px 10px #000000;
    }
  table tbody tr {
    background-color: #424242;
    border: none;
}
  .trSeperator {
    box-shadow: none !important;
}
  .accordianSettings ul li {
    border-top: none;
}
  a {
    color: #fff;
}
    #MangaPageContent > table {
    background-color: #424242;
}
  #searchResults .bigLetter {
    padding-left: 15px;
}
.ui-widget-content {
    background: #424242;
    color: #FFFFFF;
}
    .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: #424242;
    border: 0px solid #FFFFFF;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
}
    #allIdeas {
        background-color: #212121;
    }
  #allIdeas td:last-child {
    background: #424242;
    color: #FFFFFF;
}
  #MangaPageContent {
    background-color: #212121;
}
  #descriptionJoin .joinBox {
background-color: #424242;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
    border-radius: 10px;
}
  .defaultForm {
border: 0px solid #d0d0d0;
background-color: #545454;
    color: white;
}
  .detailsChapter .popupWrap {
border: 0px solid #dbdbdb;
background: #424242;
    box-shadow: 0px 0px 25px rgba(0, 0, 0, 0.5);
}
  .detailsChapter .arrow-left {
    display: none;
}
  .ui-dialog .ui-dialog-content {
background-color: #424242;
}
  #loginBox, #passwordBox, #subscriptionBox {
    background-color: #424242;
}
  #header #loggedMenuList {
background-color: #424242;
}
#header #loggedMenuList .heading {
    background-color: #4e4e4e;
}
#header #loggedMenuList ul li a:hover {
    background-color: #999;
}
  .title {
    background-color: #424242;
}
#searchBar input {
border: 1px solid #424242;
    background-color: #424242;
}
  .showTeamButton {
    background-color: #5d5d5d;
}
  .ui-widget-content a {
    color: #fff;
}
  .commentWrap .userComment {
background: #424242;
}
  .ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
background: #D34836;
    outline: none;
}
    .image > tbody:nth-child(1) > tr:nth-child(1){
      background-color: transparent !important;
    }
  table tbody tr:nth-child(2n+1) {
    background-color: #424242;
}
  .pageHeadline {
    background-color: #424242;
    color: #fff;
    border-bottom: 1px solid #616161;
}
  #rightSidebar tr {
    background-color: #424242;
}
  #rightSidebar tr:nth-child(2n+2) {
    background-color: #424242;
}
  .firstChapterAvailable {
    background-color: #424242;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
}
  .ui-widget-header {
    background: #424242;
}
  #mangaItem {
    color: #fff;
}
}
