css:
@-moz-document domain("namu.wiki") {
  /* navbar */
  .senkawa .navbar-wrapper {
      background-color: /*[[maincolor]]*/ !important;
  }
  .senkawa .navbar {
      background-color: /*[[maincolor]]*/ !important;
  }
  .senkawa .navbar-nav .nav-link:hover {
      background-color: /*[[hovercolor]]*/ !important;
  }

  /* bottom updown button */
  .senkawa .nav-control {
      background-color: /*[[maincolor]]*/ !important;
  }

  /* main page div box */
  .wiki-table[style*="background-color:white;width: 100%;border: 2px solid #00a495;"] {
      border: 2px solid /*[[maincolor]]*/ !important;
  }
  .wiki-table[style*="width: 100%;border: 2px solid #00a495;"] {
      border: 2px solid /*[[maincolor]]*/ !important;
  }
  .wiki-table[style*="width: 100%;border: 2px solid #00A495;"] {
      border: 2px solid /*[[maincolor]]*/ !important;
  }
  td[style*="background-color:#00a495;"] {
      background-color: /*[[maincolor]]*/ !important;
  }
  td[style*="background-color:#00A495; width:50%; text-align:center;"] {
      background-color: /*[[maincolor]]*/ !important;
  }
  td[style*="background-color:#00A495; text-align:center;"] {
      background-color: /*[[maincolor]]*/ !important;
  }
  font[color*="#00a495"] {
      color: /*[[maincolor]]*/ !important;
  }
  .wiki-color[style*="color:#00a495"] {
      color: /*[[maincolor]]*/ !important;
  }
  .wiki-color[style*="color:#00A495"] {
      color: /*[[maincolor]]*/ !important;
  }

  /* border top div */
  div[style*="border:1px solid gray; border-top:5px solid #00a495; padding:12px;"] {
      border-top:5px solid /*[[maincolor]]*/ !important;
  }
  div[style*="border:1px solid gray; border-top:5px solid #00A495; padding:12px;"] {
      border-top:5px solid /*[[maincolor]]*/ !important;
  }
  div[style*="border:1px solid gray; border-top:5px solid #00a495; padding:12px; margin-bottom:0px;"] {
      border-top:5px solid /*[[maincolor]]*/ !important;
  }
  div[style*="border:1px solid gray; border-top:5px solid #00A495; padding:12px; margin-bottom:0px;"] {
      border-top:5px solid /*[[maincolor]]*/ !important;
  }

  /* quote box */
  .wiki-quote {
      border-left: 5px solid /*[[maincolor]]*/ !important;
  }

  /* discuss owner color */
  .senkawa .wiki-article .res .r-head.first-author {
      background-color: /*[[ownercolor]]*/ !important;
  }
}
