css:
@-moz-document regexp("http://lisge.com/kk/(k|br|tb)/.+") {
  body,
  .HK1, .HK2 {
    font-size: 14px;
  }

  .HK1, .HK2,
  i.Z4, i.Y4,
  .BAA1, .BAA2 {
    display: block;
    transform: rotate(0.028deg);
  }

  .HK1 + br, .HK2 + br,
  i.Z4 + br, i.Y4 + br,
  .BAA1 + br, .BAA2  + br {
    display: none;
  }

  .BA1, .BA2,
  .BS1, .BS2 {
    font-size: 12px;
  }

  .F2 .BS1,
  .F2 .BS2 {
    font-size: inherit;
  }

  .C4 {
    font-size: 13px;
    font-weight: bold;
  }

  body .F3 { font-size: 14px; }
  body .F3i{ font-size: 14px; }
  body .F4 { font-size: 16px; }
  body .F4i{ font-size: 16px; }
  body .F5 { font-size: 20px; }
  body .F5i{ font-size: 20px; }
  body .F6 { font-size: 24px; }
  body .F6i{ font-size: 24px; }
  body .F7 { font-size: 32px; }
  body .F7i{ font-size: 32px; }
}
