css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("https://makemeapassword.org/") {
    
a:hover,a:focus {
	color: #820202 !important;
    text-decoration:underline;
}
    
a:hover {
	color: #820202 !important;
}
    

body {
    font-family: sans-serif !important;
    font-size: 14px;
    font-weight: normal !important;
    line-height: 1.428571429;
    background-color: #333333 !important;
    margin: 0;
}
    

.btn-default {
    background-color: #575757 !important;
    background-image: -webkit-linear-gradient(top,#fff 0,#e0e0e0 100%) !important;
    background-image: linear-gradient(to bottom,#574c4c 0,#968383 100%) !important;
    background-repeat: repeat-x;
    border-color: #484848 !important;
    text-shadow: 0 1px 0 #545454 !important;
}
    
.btn-primary {
    background-color: #242424 !important;
    background-image: -webkit-linear-gradient(top,#428bca 0,#2d6ca2 100%) !important;
    background-image: linear-gradient(to bottom,#725257 0,#333 100%) !important;
    background-repeat: repeat-x;
    border-color: #393939 !important;
    color: #d3d3d3 !important;
    text-shadow: 0 -1px 0 rgb(0, 0, 0) !important;
}
    
.container {
    width: 970px;
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.container-body {
    background-color: #141212 !important;
    border-radius: 0px 0px 0 0 !important;
    color: #a7a7a7 !important;
    padding-bottom: 20px;
    -webkit-box-shadow: 4px 4px 4px rgba(50,50,50,.4);
    -moz-box-shadow: 4px 4px 4px rgba(50,50,50,.4);
    box-shadow: 4px 4px 4px rgba(50,50,50,.4);
}

.jumbotron {
    background-color: #820202 !important;
    color: #d3d3d3 !important;
    margin: 0;
    padding-top: 48px;
    padding-bottom: 48px;
    padding: 30px;
    margin-bottom: 30px;
    font-size: 21px;
    font-weight: 200;
    line-height: 2.1428571435;
    color: inherit;
}
   
.label {
    display: inline;
    padding: .1em .5em .1em !important;
    font-size: 100%;
    font-weight: bold;
    line-height: 1;
    color: #d3d3d3 !important;
    text-align: center;
    white-space: nowrap;
    vertical-align: baseline;
    border-radius: .1em !important;
}

.label-default {
    background-color: #333 !important;
}
    
.navbar-default {
    background-image: -webkit-linear-gradient(top,#393939 0,#333333 100%) !important;
    background-image: linear-gradient(to bottom,#393939 0,#333333 100%) !important;
    background-repeat: repeat-x;
    border-radius: 0px;
}

.navbar-brand.active {
    background-color: #323232 !important;
}

.navbar-brand {
    background-color: #323232 !important;
}
    
.navbar-default .navbar-brand {
    color: #d3d3d3 !important;
}

.navbar-brand {
    margin-right: 1px !important;
}

.navbar-brand, .navbar-nav > li > a {
    text-shadow: 0 1px 0 rgb(0, 0, 0) !important;
}
    
.navbar-default .navbar-nav > li > a {
    color: #d3d3d3 !important;
}
    
.navbar-brand {
    float: left;
    padding: 15px 15px;
    font-size: 18px;
    line-height: 20px;
}

footer {
    background-color: #535353 !important;
    padding: 20px 0 30px 0;
    font-size: 85%;
    border-top: 1px solid #bbb;
}
}
