  By David Buchanan
*/

@-moz-document domain("speedof.me") {
  
  html, body {
    background-color: #EEE;
    overflow: hidden;
  }
  
  body {
    margin-top: 10vh;
  }
  
  body > * {
    display: none;
  }
  
  body > .pane {
    display: inherit;
    max-width: 1024px;
    margin: auto;
    background-color: #111;
    box-shadow: 5px 5px 15px #000;
  }
  
  .adpane_left, .adpane_right {
    display: none;
  }
  
  .msgCol {
    background-image: initial !important;
    background-color: transparent;
    color: #FFF;
    text-align: center;
  }
  
  svg > rect {
    display: none !important;
  }
  
  #pieContainer svg g.highcharts-tracker > path:first-child {
    fill: #BBB;
  }
  
  #pieContainer svg g.highcharts-tracker > path:nth-child(2) {
    fill: transparent;
  }
  
  #btnStart, #btnShare {
    border: none !important;
  }
}
