/* i really want this to be global */

@-moz-document url(https://exelab.ru) {
    body, .gbody, table{
    background-color: black !important;
    color: green  !important;
    font-family: Verdana,Geneva,Arial,Helvetica,sans-serif;
  }
  i{
    color: #096 !important;
  }
  b>font{
    color: floralwhite !important;
  }
  div#dvd,/* .gbody tbody tr:nth-child(9), .gbody tbody tr:nth-child(10), .gbody tbody tr:nth-child(11),*/ .hl_header, 
    tbody tr:first-child td a img:last-child{
    display: none !important;
  }
  div#logo {
    position: static !important;  
  }
  div#logo a img, tbody tr:first-child td a img:first-child{
    margin-left: auto !important;
    margin-right: auto !important;
    display: block !important;
  }
  tbody tr:first-child td div:last-child{
    height: 10px !important;    
  }
  .txtSm, .caption1 div, table.forums{
    color: silver !important;
  }
  .textForm{
    padding: 5px !important;
    background: #333 !important;
    margin: 0 auto !important;     
    opacity: 0.7 !important; 
    filter: alpha(Opacity=70) !important;
    color: silver !important;
  }
  .inputButton{
    background: black !important;
    color: lime !important;
  }
  small{
    color: lime !important;
  }
  .tbStBar{
    padding: 5px !important;
    background: #222 !important;
    margin: 0 auto !important;    
    opacity: 0.7 !important; 
    filter: alpha(Opacity=70) !important;
  }
  .tg, .caption2, .caption6{     
    padding: 5px !important;
    background: #111 !important;
    margin: 0 auto !important;    
    opacity: 0.7 !important; 
    filter: alpha(Opacity=70) !important;
    border: none !important;
  }
  .caption4, .caption7, .caption5, input.textForm{     
    padding: 5px !important;
    margin: 0 auto !important;
    background: #111 !important;
    opacity: 0.7 !important; 
