css:
@-moz-document domain("www.frandroid.com") {
#comments-button .gradient {
    background: linear-gradient(to bottom, transparent 0%, rgba(50, 50, 50, 0.7) 57%, #2d2d2d 100%);
}
#ctBoc {
    margin-top: 60px !important;
}
#disqus_thread {
    max-width: 100%;
}
.bg-content,
.archive.category-test .bg-content,
.archive.category-comment-faire .bg-content,
#main {
    background: #2d2d2d;
}
#main .sponsoring-branding-disclamer {
    border-radius: 3px;
    color: #fff;
}
#main .sponsoring-branding-top span {
    border-radius: 3px;
    color: #000;
    padding: 5px 20px;
}

#humanoid_top_area #new_home_zh_container .new_home_bloc_zh_container .home_bloc_zh .bloc-title-zh .post-label {
    color: #000;
    border-radius: 5px;
}

#main div,
#main p,
h1,
#main span,
#breadcrumb a,
.publisher-anchor-color p,
.post-header .post-info-breadcump #breadcrumb a {
    color: #838383;
}
#main p.wp-caption-text {
    margin-top: 5px;
    padding: 10px 0px 0px 0px;
}
#main p {
    margin: 25px 0px;
}
.larger-container {
    width: 1280px;
}
.post .pre-ad-container {
    max-width: 100%;
    width: 100%;
}
.post-preview-container .post-preview {
    border-radius: 3px;
    height: 140px;
    margin-bottom: 20px;
}
.post-preview-container .post-preview .text-container {
    padding-left: 25px;
    padding-top: 25px;
   width: 745px;
}
.post-preview-container .post-preview .text-container .cat-comments-container {
    padding-left: 25px;
}
.post-preview-title {
    font-size: 20px;
}
.single-post-container,
.page-type {
    box-shadow: none;
}
.span8 {
    width: 100%;
}
.yarpp-related,
.offer-shortcode-container,
.share-zone,
.newsletter_form,
.block-after-single,
.single-pagination,
#trending-bar,
#post-share-bar,
#placement-bottom,
.post-list,
.home-sidebar,
aside,
#footer,
#placement-bottom .post-list,
.post-footer-author .post-footer-author-links {
    display: none !important;
}
.bottom-sidebar {
    max-height: 1px;
}
#footer,
#placement-bottom .post-list {
    display: none;
}
.post-top-area h1 {
    border-top: 1px solid #9ed44b;
    margin-top: 15px;
}
.publisher-anchor-color,
.post-message p,
.post-content .post-message p:last-child,
.post-content .post-message p {
    color: #fff !important;
}
.universe-preview-container .universe-title,
.block-after-single.recent .universe-preview-container .universe-title {
    color: #fff;
}
@media (min-width: 1000px) {
    .universe-preview-container .universe-previews>li, .block-after-single.recent .universe-preview-container .universe-previews>li {
        width: 65%;
    }
}
.universe-preview-container .universe-title .line,
.block-after-single.recent .universe-preview-container .universe-title .line {
    border-bottom: 1px solid #fff;
}
.home .bg-content {
    background: none;
}
.post-preview-container .post-preview .text-container .post-preview-title {
    font-size: 20px;
    line-height: 24px;
}
h1,
h2,
h3,
h4 {
    font-family: 'montserrat';
    font-weight: 500;
}
.bottom-sidebar,
#hubs-trends,
.social,
.post-list .discovery-main,
.home-sidebar .container_native_sidebar,
.single-sidebar .container_native_sidebar {
    display: none !important;
}
ul.discovery-posts {
    max-height: 1px;
    display: none !important;
}
.post-top-area h1 {
    color: #fff;
}
#index-menu ul li a span {
    color: #000;
}
#index-menu #index-menu-action {
    background: #9ed44b;
}
.post-footer-author .post-footer-author-avatar img {
    max-width: 110px;
}
.post-footer-author .post-footer-author-avatar {
    width: 15%;
}
.post-message p,
body {
    color: #fff !important;
}
#disqus_thread {
    border-radius: 3px;
    background: #e9e9e9 !important;
    padding: 25px 50px 25px 50px;
}
}
