css:
@-moz-document url-prefix("https://app.roll20.net/editor/") {
/* GLOBAL */
body {
    overflow: hidden!important;
}

p,
label {
    color: #ccc !important;
}


input,
select,
textarea,
.btn {
    color: #ccc !important;
    background: #212121 !important;
    border-color: #777 !important;
    border-radius: 0;
}

.btn {
    background: #333 !important;
    text-shadow: none;
}

select .btn {
    cursor: pointer;
}


table,
td,
code {
    background-color: #212121!important;
    border-color: #424242!important;
    color: #ffffff!important;
}

.active > a {
    background-color: #212121!important;
}
.ui-dialog-titlebar-close {
    background-color: red!important;
}



/* BODY */
body {
    background-image: url('http://www.publicdomainpictures.net/pictures/30000/velka/black-background.jpg')!important;
}
.inlinerollresult.fullcrit {
    border-color: #247305 !important;
    background-color: #247305 !important;
}
.diceroll.critsuccess .didroll {
    color: #247305!important;
}
.diceroll .didroll {
    text-shadow: -1px -1px 1px #000, 1px -1px 1px #000, -1px 1px 1px #000, 1px 1px 1px #000;
    color: white;
}
.inlinerollresult.fullfail {
    border-color: #B31515!important;
    background-color: #B31515 !important;
}
.diceroll.critfail .didroll {
    color: #B31515!important;
}
@-webkit-keyframes rainbow {
    from {
        -webkit-filter: hue-rotate(10deg);
        filter: hue-rotate(10deg);
    }
    to {
        -webkit-filter: hue-rotate(360deg);
        filter: hue-rotate(360deg);
    }
}
.sheet-rolltemplate-default caption {
    background-color: #702082!important;
}



/* MENUS */

/*  Navigation Controls */
#zoomslider {
    background-color: #333 !important;
    border-radius: 0;
    border: none;
    top: 50px !important;
}
#zoomslider .ui-slider-handle {
    background-color: #444 !important;
    border-radius: 0;
    border-color: #666 !important;
}

#sidebarcontrol {

    padding: 6px !important;
    text-align: center !important;
    border: none !important;
    font-size: 12px !important;
    background-color: #111 !important;
    top: 10px !important;
    margin-right: 25px;
    transform: none;
    opacity: .8;
}

#sidebarcontrol .fonticon-menu {
    color: #aaa !important;
}




/* Toolbar */
#floatingtoolbar {
    background-color: rgba(0, 0, 0, .9) !important;
    border: none !important;
    box-shadow: none !important;
    width: 50px !important;
}

#floatingtoolbar div.submenu {
    left: 50px !important;
    padding: 0 !important;
    top: 0 !important;
}

#floatingtoolbar div.submenu .pictos,
#floatingtoolbar div.submenu .pictosthree,
#floatingtoolbar div.submenu .pictostwo {
    left: 15px !important;
    position: absolute !important;
    text-align: center !important;
    width: 20px !important;
}

#floatingtoolbar div.submenu ul {
    border: 0 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    padding: 0 !important;
    width: 200px !important;
}

#floatingtoolbar div.submenu ul li {
    color: #ccc !important;
    padding-left: 45px !important;
    padding-right: 20px !important;
}

#floatingtoolbar div.submenu ul li span {
    padding: 0 !important;
}

#floatingtoolbar div.submenu ul li:hover {
    background-color: #222 !important;
}

#floatingtoolbar li {
    border: 0 !important;
    color: #ccc !important;
    height: 50px !important;
    line-height: 50px !important;
    padding: 0 !important;
}

#floatingtoolbar li.activebutton,
#floatingtoolbar li:hover {
    background-color: white !important;
    color: #111 !important;
}

#floatingtoolbar li:last-child {
    border-bottom: none !important;
}

#floatingtoolbar ul {
    box-shadow: none !important;
}

#floatingtoolbar #measure li.activerulermode {
    background-color: #444 !important;
    color: #fff !important;
}





/* SIDEBAR */
#rightsidebar .ui-tabs-panel {
    top: 70px !important;
}

#rightsidebar .ui-tabs-panel#textchat {
    position: absolute !important;
    top: 50px !important;
    height: initial !important;
    bottom: 150px !important;
}



/* Main Menu */
.tabmenu {
    height: 50px !important;
    position: absolute !important;
    left: 0 !important;
    right: 0 !important;
    margin: 0 !important;
    padding: 0 !important;
    top: 0 !important;
    background-color: #212121!important;
}

.tabmenu li {
    height: 50px !important;
    width: 16.667% !important;
    text-align: center !important;
    top: 0 !important;
    background-color: #212121!important;
}

.tabmenu li a {
    padding: 0 !important;
    margin: 0 !important;
    line-height: 50px !important;
    border: 0 !important;
    width: 100% !important;
    background-color: #212121!important;
    color: #888888 !important;
    text-shadow: none!important;
}

.tabmenu li:hover a {
    color: white !important;
    border: none !important;
}
.tabmenu li.ui-state-active a {
    color: white !important;
    border-bottom: 1px solid white;
}



/* Content */
.content h3 {
    color: #fff;
}
.content > p > a {
    color: #000!important;
}



.submenu ul,
#secondary-toolbar,
#page-toolbar,
.d20contextmenu ul li ul.submenu {
    background-color: rgba(16, 16, 16, 0.80)!important;
    color: #fff!important;
}
.submenu li {
    color: #fff!important;
}

.markermenu {
    background-color: rgba(16, 16, 16, 0.8)!important;
}
.statusicon {
    border: 4px solid rgba(16, 16, 16, 0)!important;
}



.inner select,
#path_width,
#font-family,
#font-size,
.scale_units,
.diagonaltype,
.gridtype,
.pagejukeboxtrigger,
#sidebarcontrol,
#speakingas,
#jukebox_filter,
#ttaudio_filter,
.deckpilemode,
.discardpilemode,
.cardsplayed,
.treatasdrawing,
#videoplayersize,
.markers_position,
.videotech,
.represents,
.bar1_link,
.bar2_link,
.bar3_link,
.dicetype,
.targettype,
.ui-dialog-content > p > select,
.chzn-results,
.marketplacead,
.expandsearch,
.googleresult,
.dd-content,
.sheet-footer,
.sheet-roll-cell,
.sheet-table-row,
.sheet-table-header,
.charsheet textarea,
.sheet-options,
.sheet-spellcaster-button,
.repcontrol_add,
.sheet-text-button,
.redactor_editor,
.redactor_editor:focus {
    background-color: #212121!important;
    color: #ccc!important;
}

.ui-slider-handle {
    background-color: #212121!important;
    background-image: none!important;
}
.ui-widget-content {
    background-color: #212121!important;
    background-image: none!important;
    color: #fff!important;
}
.d20contextmenu > ul {
    background-color: rgba(16, 16, 16, 0.8)!important;
    color: #fff!important;
}
.ui-dialog {
    border-radius: 0;
    border: none;
}
.ui-dialog-titlebar {
    border-radius: 0;
    border: none;
    border-bottom: 1px solid #424242 !important;
}

.ui-dialog-title {
    color: #fff!important;
    text-shadow: none!important;
}
.search-choice {
    background-color: #212121!important;
    background-image: none!important;
}
#initiativewindow ul li {
    background-color: #212121!important;
}
#initiativewindow ul li:first-child {
    background-color: #0286d5!important;
}






.textchatcontainer .rolled {
    border-radius: 0 !important;
    width: 40px !important;
    height: 40px !important;
}

#textchat-input {
    background-color: #212121 !important;
    border-top: none;
    padding-top: 10px;
}
#textchat-input .ui-autocomplete-input {
    background-color: #151515 !important;
    border: none;
    border-radius: 0;
}
#textchat-input .btn,
#textchat-input .chosen {
    background-image: none;
    background-color: #424242 !important;
    border-radius: 0;
    border: none;
    color: #fff!important;
}
#textchat .message.system .spacer {
    background-color: #666!important;
}
#textchat .formula {
    background-color: #212121!important;
    border: 1px solid #fff!important;
}
#textchat .message {
    background-color: #212121!important;
}
#textchat .message.general.you,
#textchat .message.rollresult.you {
    background-color: #212121 !important;
}

#textchat .message.system {
    background-color: #002903!important;
}
#textchat .message.desc {
    background-color: #ccc!important;
    color: #111!important;
}
#textchat .message.error {
    background-color: rgba(255, 0, 0, 0.64)!important;
}
#textchat .message.private {
    background-color: rgba(255, 92, 1, 0.33)!important;
}
#textchat .message .spacer {
    background-color: #444;
    margin-top: 10px;
    margin-bottom: 10px;
}
#textchat .message.you .spacer {
    background-color: #005f82;
}

.searchbox,
.searchtype,
.namecontainer,
.folder-title {
    background-color: #212121!important;
    color: #fff!important;
}


/* .btn.pictos.alltags.trigger {
	background-color: #212121!important;
	background-image: -webkit-linear-gradient(top, #212121, #212121)!important;
	text-shadow: none!important;
	color: #fff!important;
} */
.attrname:hover {
    background-color: #212121!important;
    border: 1px solid #fff!important;
}
.abil {
    background-color: #212121!important;
}
.charactereditor .avatar,
.dropbox,
.charactereditor .defaulttoken,
.tokenslot {
    background-color: #212121!important;
    border: 1px solid #fff!important;
}
.remove a {
    color: #08c!important;
}
.redactor_dropdown {
    background-color: #212121!important;
}
#secondary-toolbar {
    opacity: 1!important;
}
.chzn-choices {
    background-color: #212121!important;
    background-image: none!important;
}
#redactor_toolbar_2 a,
#redactor_toolbar_3 a {
    color: #000!important;
}
textarea.texteditor {
    background-color: rgba(0, 0, 0, 0)!important;
}
.dd-handle,
.dd-handle:before {
    background-color: #212121!important;
    color: #fff!important;
}
.playerdots {
    background-color: rgba(255, 255, 255, 0)!important;
}
.nav > li > a {
    background-color: #212121!important;
    border-bottom: 1px solid #ddd;
}
.nav > li > a:hover {
    border: none!important;
}
#macrobar {
    height: 38px!important;
}

/* fix for roll20 portait position*/
#playerzone.pz-top
{
    display: inline !important;
}

#playerzone.showmacrobar {
    padding-bottom: 52px!important;
}
.shuffle {
    color: #000000!important;
}
#showqrcode.btn {
    color: #000!important;
}
#color_selector {
    background-color: #212121!important;
}

#exitroll20game {

    text-shadow: none!important;
    color: #ccc!important;
}




/* CHARACTER SHEET */
.sheet-rolltemplate-pf_generic .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_ability .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_defense .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_attack .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_spell .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_block .sheet-roll-group2 .sheet-roll-row .sheet-roll-cell:last-child,
.sheet-rolltemplate-pf_attack .sheet-roll-groupi .sheet-roll-row .sheet-roll-cell:last-child {
    padding: 1px 5px !important
}

.sheet-rolltemplate-pf_spell .sheet-dmg,
.sheet-rolltemplate-pf_spell .sheet-dmg-x,
.sheet-rolltemplate-pf_spell .sheet-crit-dmg,
.sheet-rolltemplate-pf_spell .sheet-crit-dmg-x,
.sheet-rolltemplate-pf_attack .sheet-dmg,
.sheet-rolltemplate-pf_attack .sheet-dmg-x,
.sheet-rolltemplate-pf_attack .sheet-crit-dmg,
.sheet-rolltemplate-pf_attack .sheet-crit-dmg-x {
    text-indent: 0 !important;
}

.sheet-roll-table {
    background-color: #424242 !important;
    border: none !important;
    position: relative;
}

.sheet-roll-table.sheet-rounded {
    border-radius: 0 !important;
}
.sheet-roll-table div:last-child .sheet-roll-cell:not(.sheet-dmg) {
    padding-bottom: 5px !important;
}

.sheet-roll-table .sheet-roll-header {
    background-color: #424242 !important;
    border: none;
    height: 25px !important;
    line-height: 25px !important;
}
.sheet-roll-table .sheet-roll-row,
.sheet-roll-table .sheet-left,
.sheet-roll-table .sheet-roll-cell {
    background-color: #424242 !important;
    font-size: 13px;
    font-weight: normal !important;
    padding-left: 5px !important;
}
.sheet-roll-table .sheet-roll-cell strong {
    font-weight: normal !important;
}
.sheet-roll-table .sheet-roll-header a {
    text-decoration: none;
    background-color: #424242 !important;
}



.charsheet .sheet-emphasize-field {
    background-color: transparent;
}
.charsheet .sheet-right-border {
    border: none !important;
}
.charsheet h2,
.charsheet h3 {
    color: lightgray !important;
}
.charsheet .sheet-tab {
    border-radius: 0 !important;
    background-color: #424242 !important;
}
.charsheet .sheet-tabinput.sheet-tab0:checked + .sheet-tab.sheet-tab0,
.charsheet .sheet-tabinput.sheet-tab1:checked + .sheet-tab.sheet-tab1,
.charsheet .sheet-tabinput.sheet-tab2:checked + .sheet-tab.sheet-tab2,
.charsheet .sheet-tabinput.sheet-tab3:checked + .sheet-tab.sheet-tab3,
.charsheet .sheet-tabinput.sheet-tab4:checked + .sheet-tab.sheet-tab4,
.charsheet .sheet-tabinput.sheet-tab5:checked + .sheet-tab.sheet-tab5,
.charsheet .sheet-tabinput.sheet-tab6:checked + .sheet-tab.sheet-tab6,
.charsheet .sheet-tabinput.sheet-tab7:checked + .sheet-tab.sheet-tab7,
.charsheet .sheet-tabinput.sheet-tab8:checked + .sheet-tab.sheet-tab8,
.charsheet .sheet-tabinput.sheet-tab9:checked + .sheet-tab.sheet-tab9,
.charsheet .sheet-tabinput.sheet-tab10:checked + .sheet-tab.sheet-tab10,
.charsheet .sheet-tabinput.sheet-tab11:checked + .sheet-tab.sheet-tab11 {
    background: #6d6d6d !important;
    color: White;
}
.charsheet .sheet-tabinput.sheet-tab99:checked ~ .sheet-tab.sheet-tab99 {
    background-color: transparent !important;
    color: White;
    width: 100% !important;
}

.charsheet .sheet-nontable-name.sheet-secondary-table-name,
.charsheet .sheet-table-name.sheet-secondary-table-name {
    border-radius: 0 !important;
    background-color: #424242 !important;
}

.charsheet .sheet-table-name,
.charsheet .sheet-nontable-name,
.charsheet .sheet-secondary-table-name {
    border-radius: 0 !important;
    background-color: #424242 !important;
}
.charsheet .sheet-table-cell img {
    -webkit-filter: invert(100%);
}
.charsheet .sheet-table.sheet-sect.sheet-wide-numbers.sheet-load {
    background-color: transparent !important;
}
.charsheet .sheet-subnav-hr {
    border-color: #424242 !important;
}


.attributes {
    color: white !important;
}
}

@-moz-document url-prefix("https://app.roll20.net/editor/popout") {
/* GLOBAL */
